import React from 'react';
import { Card, CardContent, Typography, LinearProgress, Box, IconButton, Link } from '@mui/material';
import { Pattern } from '../data/patterns';
import StarIcon from '@mui/icons-material/Star';
import StarBorderIcon from '@mui/icons-material/StarBorder';
import CodeIcon from '@mui/icons-material/Code';
import LaunchIcon from '@mui/icons-material/Launch';
import CheckCircleOutlineIcon from '@mui/icons-material/CheckCircleOutline';
import PlayCircleOutlineIcon from '@mui/icons-material/PlayCircleOutline';

interface PatternCardProps {
  pattern: Pattern;
  onToggleStar?: () => void;
  isStarred?: boolean;
}

const PatternCard: React.FC<PatternCardProps> = ({ pattern, onToggleStar, isStarred }) => {
  const progress = (pattern.progress.completed / pattern.progress.total) * 100;

  return (
    <Card 
      sx={{
        minWidth: 275,
        mb: 2,
        position: 'relative',
        bgcolor: '#212121', // Dark background
        color: '#e0e0e0', // Light text
        boxShadow: 3
      }}
    >
      <CardContent>
        <Box display="flex" justifyContent="space-between" alignItems="center" sx={{ mb: 2 }}>
          <Box display="flex" alignItems="center">
             <Typography variant="h5" component="div" sx={{ mr: 1 }}>
              {pattern.name}
            </Typography>
             <Link href="#" target="_blank" rel="noopener noreferrer" color="inherit">
                <LaunchIcon fontSize="small" />
             </Link>
          </Box>

          <IconButton onClick={onToggleStar} size="small" sx={{ color: isStarred ? '#ffeb3b' : '#e0e0e0' }}>
            {isStarred ? <StarIcon /> : <StarBorderIcon />}
          </IconButton>
        </Box>

        <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
          Progress: {pattern.progress.completed} / {pattern.progress.total}
        </Typography>
        <LinearProgress 
          variant="determinate" 
          value={progress} 
          sx={{
            mt: 1,
            height: 8,
            borderRadius: 4,
            bgcolor: '#424242', // Darker background for progress bar track
            '& .MuiLinearProgress-bar': { 
              bgcolor: '#66bb6a' // Green color for progress bar
            }
          }}
        />
        
        {/* Table-like structure for questions */}
        <Box sx={{ mt: 3 }}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', pb: 1, borderBottom: '1px solid #424242' }}>
            <Typography variant="caption" sx={{ width: '10%' }}>Status</Typography>
            <Typography variant="caption" sx={{ width: '40%' }}>Problem</Typography>
            <Typography variant="caption" sx={{ width: '20%' }}>Difficulty</Typography>
            <Typography variant="caption" sx={{ width: '20%' }}>Solution</Typography>
            <Typography variant="caption" sx={{ width: '10%', textAlign: 'center' }}>Star</Typography>
          </Box>
          {pattern.questions.map((question, index) => (
            <Box 
              key={index} 
              sx={{
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center',
                py: 1,
                borderBottom: index === pattern.questions.length - 1 ? 'none' : '1px solid #424242',
                '&:hover': { bgcolor: '#2c2c2c' } // Hover effect
              }}
            >
              {/* Status */}
              <Box sx={{ width: '10%' }}>
                 {question.status === 'Completed' && <CheckCircleOutlineIcon sx={{ color: '#66bb6a' }} fontSize="small" />}
              </Box>
              
              {/* Problem */}
              <Box sx={{ width: '40%', display: 'flex', alignItems: 'center' }}>
                <Link 
                  href="#" 
                  target="_blank" 
                  rel="noopener noreferrer"
                  color="inherit"
                  underline="none"
                  sx={{ 
                    '&:hover': { textDecoration: 'underline' },
                    mr: 0.5
                   }}
                >
                  {question.name}
                </Link>
                <LaunchIcon sx={{ fontSize: 12, color: '#757575' }} />
              </Box>

              {/* Difficulty */}
              <Typography 
                variant="body2" 
                sx={{
                  width: '20%',
                  color: 
                    question.difficulty === 'Easy' ? '#66bb6a' : 
                    question.difficulty === 'Medium' ? '#ffa726' : '#ef5350'
                }}
              >
                {question.difficulty}
              </Typography>
              
              {/* Solution */}
              <Box sx={{ width: '20%', display: 'flex', gap: 1 }}>
                 <Link href={question.solution} target="_blank" rel="noopener noreferrer" color="inherit">
                    <CodeIcon fontSize="small" />
                 </Link>
                 <Link href="#" target="_blank" rel="noopener noreferrer" color="inherit"> {/* Placeholder for YouTube link */}
                    <PlayCircleOutlineIcon fontSize="small" />
                 </Link>
              </Box>

              {/* Star */}
              <Box sx={{ width: '10%', textAlign: 'center' }}>
                {/* Star icon is handled by the IconButton wrapper in the header */}
              </Box>

            </Box>
          ))}
        </Box>
      </CardContent>
    </Card>
  );
};

export default PatternCard;
