import React from 'react';
import { Card, CardContent, Typography, LinearProgress, Box, IconButton, Link } from '@mui/material';
import { Pattern } from '../data/patterns';
import StarIcon from '@mui/icons-material/Star';
import StarBorderIcon from '@mui/icons-material/StarBorder';
import CodeIcon from '@mui/icons-material/Code';
import LaunchIcon from '@mui/icons-material/Launch';
import CheckCircleOutlineIcon from '@mui/icons-material/CheckCircleOutline';
import PlayCircleOutlineIcon from '@mui/icons-material/PlayCircleOutline';

interface PatternCardProps {
  pattern: Pattern;
  onToggleStar?: () => void;
  isStarred?: boolean;
}

const PatternCard: React.FC<PatternCardProps> = ({ pattern, onToggleStar, isStarred }) => {
  const progress = (pattern.progress.completed / pattern.progress.total) * 100;

  // Function to get difficulty color
  const getDifficultyColor = (difficulty: 'Easy' | 'Medium' | 'Hard') => {
    switch (difficulty) {
      case 'Easy':
        return '#66bb6a'; // Green
      case 'Medium':
        return '#ffa726'; // Orange
      case 'Hard':
        return '#ef5350'; // Red
      default:
        return '#e0e0e0'; // Default light gray
    }
  };

  return (
    <Card 
      sx={{
        mb: 2,
        position: 'relative',
        bgcolor: '#212121', // Dark background
        color: '#e0e0e0', // Light text
        boxShadow: 3,
        borderRadius: 2 // Slightly rounded corners
      }}
    >
      <CardContent>
        {/* Header with Pattern Name, Link, and Star */}
        <Box display="flex" justifyContent="space-between" alignItems="center" sx={{ mb: 2 }}>
          <Box display="flex" alignItems="center">
            <Typography variant="h5" component="div" sx={{ mr: 1, fontWeight: 'bold' }}>
              {pattern.name}
            </Typography>
            {/* Placeholder link for pattern */}
            <Link href="#" target="_blank" rel="noopener noreferrer" color="inherit" sx={{ lineHeight: 1 }}>
              <LaunchIcon fontSize="small" />
            </Link>
          </Box>

          <IconButton onClick={onToggleStar} size="small" sx={{ color: isStarred ? '#ffeb3b' : '#bdbdbd' }}>
            {isStarred ? <StarIcon /> : <StarBorderIcon />}
          </IconButton>
        </Box>

        {/* Progress Bar */}
        <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
          Progress: {pattern.progress.completed} / {pattern.progress.total}
        </Typography>
        <LinearProgress 
          variant="determinate" 
          value={progress} 
          sx={{
            mt: 1,
            height: 8,
            borderRadius: 4,
            bgcolor: '#424242', // Darker background for progress bar track
            '& .MuiLinearProgress-bar': { 
              bgcolor: '#66bb6a' // Green color for progress bar
            }
          }}
        />
        
        {/* Table Header */}
        <Box sx={{ mt: 4, mb: 1, display: 'flex', justifyContent: 'space-between', pb: 1, borderBottom: '1px solid #424242' }}>
          <Typography variant="caption" sx={{ width: '10%', fontWeight: 'bold', color: '#bdbdbd' }}>Status</Typography>
          <Typography variant="caption" sx={{ width: '40%', fontWeight: 'bold', color: '#bdbdbd' }}>Problem</Typography>
          <Typography variant="caption" sx={{ width: '20%', fontWeight: 'bold', color: '#bdbdbd' }}>Difficulty</Typography>
          <Typography variant="caption" sx={{ width: '20%', fontWeight: 'bold', color: '#bdbdbd' }}>Solution</Typography>
          <Typography variant="caption" sx={{ width: '10%', textAlign: 'center', fontWeight: 'bold', color: '#bdbdbd' }}>Star</Typography>
        </Box>

        {/* Questions List */}
        <Box>
          {pattern.questions.map((question, index) => (
            <Box 
              key={index} 
              sx={{
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center',
                py: 1.5, // Increased vertical padding
                borderBottom: index === pattern.questions.length - 1 ? 'none' : '1px solid #424242',
                '&:hover': { bgcolor: '#2c2c2c' }, // Hover effect
                transition: 'background-color 0.2s ease-in-out' // Smooth transition
              }}
            >
              {/* Status */}
              <Box sx={{ width: '10%', display: 'flex', justifyContent: 'center' }}>
                 {question.status === 'Completed' && <CheckCircleOutlineIcon sx={{ color: '#66bb6a' }} fontSize="small" />}
                 {/* Add other status icons if needed */}
              </Box>
              
              {/* Problem */}
              <Box sx={{ width: '40%', display: 'flex', alignItems: 'center' }}>
                <Link 
                  href="#" 
                  target="_blank" 
                  rel="noopener noreferrer"
                  color="inherit"
                  underline="none"
                  sx={{ 
                    '&:hover': { textDecoration: 'underline' },
                    mr: 0.5,
                    color: '#90caf9' // Brighter link color
                   }}
                >
                  {question.name}
                </Link>
                <LaunchIcon sx={{ fontSize: 12, color: '#757575' }} />
              </Box>

              {/* Difficulty */}
              <Typography 
                variant="body2" 
                sx={{
                  width: '20%',
                  color: getDifficultyColor(question.difficulty),
                  fontWeight: 'bold'
                }}
              >
                {question.difficulty}
              </Typography>
              
              {/* Solution */}
              <Box sx={{ width: '20%', display: 'flex', gap: 1, justifyContent: 'center' }}>
                 {/* GitHub Link */}
                 {question.solution && (
                   <Link href={question.solution} target="_blank" rel="noopener noreferrer" color="inherit" sx={{ lineHeight: 1, color: '#90caf9' }}>
                      <CodeIcon fontSize="small" />
                   </Link>
                 )}
                 {/* YouTube Link Placeholder */}
                 <Link href="#" target="_blank" rel="noopener noreferrer" color="inherit" sx={{ lineHeight: 1, color: '#90caf9' }}>
                    <PlayCircleOutlineIcon fontSize="small" />
                 </Link>
              </Box>

              {/* Star (Question level - if needed later) */}
              {/* Keeping this for potential future use, but currently using pattern level star */}
              <Box sx={{ width: '10%', textAlign: 'center' }}>
                {/* <IconButton size="small" sx={{ color: question.starred ? '#ffeb3b' : '#bdbdbd' }}>
                  {question.starred ? <StarIcon fontSize="small" /> : <StarBorderIcon fontSize="small" />}
                </IconButton> */}
              </Box>

            </Box>
          ))}
        </Box>
      </CardContent>
    </Card>
  );
};

export default PatternCard;
