import React from 'react';
import {
  Box,
  Typography,
  Card,
  CardContent,
  IconButton,
  useTheme,
} from '@mui/material';
import GitHubIcon from '@mui/icons-material/GitHub';
import LinkedInIcon from '@mui/icons-material/LinkedIn';
import TwitterIcon from '@mui/icons-material/Twitter';
import Pic from '../pic.jpg';
import { motion } from 'framer-motion';

interface AuthorProps {
  darkMode: boolean;
}

const Author: React.FC<AuthorProps> = ({ darkMode }) => {
  const theme = useTheme();

  const profileImageVariants = {
    animate: {
      y: [0, -10, 0],
      transition: { duration: 2, repeat: Infinity, ease: 'easeInOut' },
    },
  };

  const sectionTitleStyle = {
    mt: 4,
    mb: 3,
    fontWeight: 'bold',
    textAlign: 'left',
    borderBottom: '2px dotted',
    borderColor: theme.palette.divider,
    pb: 1,
  };

  return (
    <Box
      sx={{
        bgcolor: darkMode ? '#1a1a1a' : '#f0f0f0',
        minHeight: '100vh',
        py: 6,
        px: 2,
        display: 'flex',
        justifyContent: 'center',
      }}
    >
      <Card
        sx={{
          maxWidth: 900,
          width: '100%',
          borderRadius: 4,
          boxShadow: 6,
          bgcolor: darkMode ? '#212121' : '#ffffff',
          p: { xs: 2, md: 4 },
        }}
      >
        <CardContent>
          {/* Profile Image and Intro */}
          <Box sx={{ textAlign: 'center', mb: 6 }}>
            <motion.div variants={profileImageVariants} animate="animate">
              <Box
                component="img"
                src={Pic}
                alt="Subham Kumar"
                sx={{
                  width: 160,
                  height: 160,
                  borderRadius: '50%',
                  objectFit: 'cover',
                  mb: 3,
                  border: `5px solid ${darkMode ? '#424242' : '#e0e0e0'}`,
                  boxShadow: 3,
                }}
              />
            </motion.div>
            <Typography variant="h4" sx={{ fontWeight: 'bold' }}>
              Hi there! I'm Subham.
            </Typography>
            <Typography variant="h6" sx={{ fontStyle: 'italic' }}>
              Developer • Problem Solver • Open Source Enthusiast
            </Typography>
          </Box>

          {/* Bio */}
          <Box sx={{ textAlign: 'left', mb: 5 }}>
            <Typography variant="body1" paragraph sx={{ lineHeight: 1.8 }}>
              I'm a final-year dual degree student at IIT (ISM) Dhanbad in Mathematics & Computing. I’m passionate about building software products, contributing to open source, and exploring web development and machine learning.
            </Typography>
            <Typography variant="body1" paragraph sx={{ lineHeight: 1.8 }}>
              I’ve mentored in open-source programs, built real-world apps, and love solving algorithmic problems. Recently, I contributed to projects at Metamask, OpenMRS, and CircuitVerse, and worked on a full-stack CDC Portal for my institute.
            </Typography>
          </Box>

          {/* Work Experience */}
          <Typography variant="h5" sx={sectionTitleStyle}>
            💼 Work Experience
          </Typography>

          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 4, mb: 6, pl: 1 }}>
            {[
              {
                role: 'Frontend Engineer Intern',
                company: 'Sportthon',
                date: 'Jan 2025 – Mar 2025',
                tech: 'React, CSS, REST API, MySQL',
                color: darkMode ? '#66bb6a' : '#4caf50',
              },
              {
                role: 'Open Source Contributor',
                company: 'Self-Directed',
                date: 'Dec 2023 – Apr 2024',
                tech: 'Projects: Metamask, CircuitVerse, OpenMRS',
                color: darkMode ? '#ffa726' : '#ff9800',
              },
              {
                role: 'Open Source Mentor',
                company: 'CircuitVerse',
                date: 'May 2024 – Aug 2024',
                tech: 'Guided contributors, reviewed PRs, improved docs',
                color: darkMode ? '#0288d1' : '#03a9f4',
              },
            ].map((exp, idx) => (
              <motion.div
                key={idx}
                initial={{ opacity: 0, x: -20 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ duration: 0.5 + idx * 0.2 }}
              >
                <Box sx={{ display: 'flex', alignItems: 'flex-start' }}>
                  <Typography
                    sx={{
                      fontWeight: 'bold',
                      mr: 2,
                      mt: '4px',
                      color: exp.color,
                      minWidth: '25px',
                    }}
                  >
                    ● →
                  </Typography>
                  <Box>
                    <Typography variant="h6" sx={{ fontWeight: 'medium' }}>
                      {exp.role}
                    </Typography>
                    <Typography variant="subtitle1" sx={{ fontStyle: 'italic' }}>
                      {exp.company}
                    </Typography>
                    <Typography variant="body2" sx={{ mb: 0.5 }}>
                      {exp.date}
                    </Typography>
                    <Typography variant="body2" sx={{ fontWeight: 'bold' }}>
                      {exp.tech}
                    </Typography>
                  </Box>
                </Box>
              </motion.div>
            ))}
          </Box>

          {/* Education */}
          <Typography variant="h5" sx={sectionTitleStyle}>
            🎓 Education
          </Typography>

          <motion.div
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.4 }}
          >
            <Box
              sx={{
                bgcolor: darkMode ? '#2c2c2c' : '#eeeeee',
                p: 3,
                borderRadius: 2,
                boxShadow: 3,
                mb: 6,
                borderLeft: `4px solid ${darkMode ? '#9575cd' : '#673ab7'}`,
              }}
            >
              <Typography variant="h6" sx={{ fontWeight: 'medium' }}>
                Integrated Master of Technology (B.Tech + M.Tech)
              </Typography>
              <Typography variant="subtitle1" sx={{ fontStyle: 'italic', mb: 1 }}>
                Mathematics and Computing, IIT (ISM) Dhanbad
              </Typography>
              <Typography variant="body2">Dec 2021 – Apr 2026</Typography>
            </Box>
          </motion.div>

          {/* Social Links */}
          <Typography variant="h5" sx={{ ...sectionTitleStyle, textAlign: 'center' }}>
            Find Me On
          </Typography>

          <Box sx={{ textAlign: 'center', mt: 2 }}>
            <IconButton
              href="https://github.com/yourusername"
              target="_blank"
              sx={{
                color: darkMode ? '#bdbdbd' : '#555555',
                '&:hover': { color: '#000', transform: 'scale(1.2)' },
                m: 1,
              }}
            >
              <GitHubIcon fontSize="large" />
            </IconButton>
            <IconButton
              href="https://linkedin.com/in/yourusername"
              target="_blank"
              sx={{
                color: darkMode ? '#bdbdbd' : '#555555',
                '&:hover': { color: '#0077b5', transform: 'scale(1.2)' },
                m: 1,
              }}
            >
              <LinkedInIcon fontSize="large" />
            </IconButton>
            <IconButton
              href="https://twitter.com/yourusername"
              target="_blank"
              sx={{
                color: darkMode ? '#bdbdbd' : '#555555',
                '&:hover': { color: '#1da1f2', transform: 'scale(1.2)' },
                m: 1,
              }}
            >
              <TwitterIcon fontSize="large" />
            </IconButton>
          </Box>
        </CardContent>
      </Card>
    </Box>
  );
};

export default Author;
