import React from 'react';
import {
  Box,
  Typography,
  Container,
  IconButton,
  useTheme
} from '@mui/material';
import GitHubIcon from '@mui/icons-material/GitHub';
import LinkedInIcon from '@mui/icons-material/LinkedIn';
import TwitterIcon from '@mui/icons-material/Twitter';
import Pic from '../pic.jpg';
import { motion } from 'framer-motion';

interface AuthorProps {
  darkMode: boolean;
}

const Author: React.FC<AuthorProps> = ({ darkMode }) => {
  const theme = useTheme();

  const profileImageVariants = {
    animate: {
      y: [0, -8, 0],
      transition: { duration: 2.5, repeat: Infinity, ease: 'easeInOut' },
    },
  };

  return (
    <Box
      sx={{
        flexGrow: 1,
        bgcolor: darkMode ? '#1a1a1a' : '#f5f5f5',
        minHeight: '100vh',
        color: darkMode ? '#e0e0e0' : '#333333',
        py: 8,
        px: 2,
      }}
    >
      <Container
        maxWidth="md"
        sx={{
          bgcolor: darkMode ? '#212121' : '#ffffff',
          p: { xs: 4, md: 6 },
          borderRadius: 3,
          boxShadow: 6,
          textAlign: 'center',
        }}
      >
        {/* Profile */}
        <Box sx={{ mb: 6 }}>
          <motion.div variants={profileImageVariants} animate="animate">
            <Box
              component="img"
              src={Pic}
              alt="Subham Kumar"
              sx={{
                width: 180,
                height: 180,
                borderRadius: '50%',
                objectFit: 'cover',
                mb: 3,
                border: `5px solid ${darkMode ? '#424242' : '#e0e0e0'}`,
                boxShadow: 3,
              }}
            />
          </motion.div>
          <Typography variant="h3" gutterBottom sx={{ fontWeight: 'bold' }}>
            Hi there! I'm Subham.
          </Typography>
          <Typography variant="h6" paragraph sx={{ fontStyle: 'italic' }}>
            Developer • Problem Solver • Open Source Enthusiast
          </Typography>
        </Box>

        {/* Bio */}
        <Box sx={{ mb: 6, textAlign: 'left' }}>
          <Typography variant="body1" paragraph sx={{ lineHeight: 1.8 }}>
            I'm a final-year dual degree student at IIT (ISM) Dhanbad in Mathematics & Computing. I’m passionate about building software products, contributing to open source, and exploring web development and machine learning.
          </Typography>
          <Typography variant="body1" paragraph sx={{ lineHeight: 1.8 }}>
            Recently, I contributed to Metamask, OpenMRS, CircuitVerse and built a full-stack CDC Portal. I'm looking for roles in frontend/backend to build scalable, impactful products.
          </Typography>
        </Box>

        {/* Work Experience */}
        <Box sx={{ mb: 6 }}>
          <Typography
            variant="h4"
            sx={{
              fontWeight: 'bold',
              textAlign: 'left',
              mb: 4,
              borderTop: `2px dotted ${darkMode ? '#757575' : '#bdbdbd'}`,
              pt: 3,
            }}
          >
            💼 Work Experience
          </Typography>

          <Box sx={{ position: 'relative', pl: 3 }}>
            {[
              {
                role: 'Frontend Engineer Intern',
                company: 'Sportthon',
                date: 'Jan 2025 – Mar 2025',
                tech: 'React, CSS, REST API, MySQL',
                color: darkMode ? '#66bb6a' : '#4caf50',
              },
              {
                role: 'Open Source Contributor',
                company: 'Self-Directed',
                date: 'Dec 2023 – Apr 2024',
                tech: 'Projects: Metamask, CircuitVerse, OpenMRS',
                color: darkMode ? '#ffa726' : '#ff9800',
              },
              {
                role: 'Open Source Mentor',
                company: 'CircuitVerse',
                date: 'May 2024 – Aug 2024',
                tech: 'Guided contributors, reviewed PRs, improved docs',
                color: darkMode ? '#0288d1' : '#03a9f4',
              },
            ].map((exp, idx) => (
              <motion.div
                key={idx}
                initial={{ opacity: 0, x: -20 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ duration: 0.4, delay: 0.1 * idx }}
              >
                <Box
                  sx={{
                    mb: 4,
                    pl: 3,
                    position: 'relative',
                    '&::before': {
                      content: '""',
                      position: 'absolute',
                      top: 6,
                      left: 0,
                      width: 12,
                      height: 12,
                      bgcolor: exp.color,
                      borderRadius: '50%',
                      border: '2px solid white',
                      boxShadow: `0 0 0 2px ${exp.color}`,
                    },
                  }}
                >
                  <Typography variant="h6" sx={{ fontWeight: 'medium' }}>
                    {exp.role}
                  </Typography>
                  <Typography variant="subtitle1" sx={{ fontStyle: 'italic' }}>
                    {exp.company}
                  </Typography>
                  <Typography variant="body2" sx={{ mb: 1 }}>
                    {exp.date}
                  </Typography>
                  <Typography variant="body2" sx={{ fontWeight: 'bold' }}>
                    {exp.tech}
                  </Typography>
                </Box>
              </motion.div>
            ))}
            {/* Vertical Line */}
            <Box
              sx={{
                position: 'absolute',
                top: 0,
                bottom: 0,
                left: '6px',
                width: '2px',
                bgcolor: darkMode ? '#616161' : '#bdbdbd',
              }}
            />
          </Box>
        </Box>

        {/* Education */}
        <Box sx={{ mb: 6 }}>
          <Typography
            variant="h4"
            sx={{
              fontWeight: 'bold',
              textAlign: 'left',
              mb: 3,
              borderTop: `2px dotted ${darkMode ? '#757575' : '#bdbdbd'}`,
              pt: 3,
            }}
          >
            🎓 Education
          </Typography>

          <motion.div
            initial={{ opacity: 0, x: 20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.5, delay: 0.4 }}
          >
            <Box
              sx={{
                bgcolor: darkMode ? '#212121' : '#eeeeee',
                p: 3,
                borderRadius: 2,
                boxShadow: 3,
                borderLeft: `4px solid ${darkMode ? '#9575cd' : '#673ab7'}`,
              }}
            >
              <Typography variant="h6" sx={{ fontWeight: 'medium' }}>
                Integrated Master of Technology (B.Tech + M.Tech)
              </Typography>
              <Typography variant="subtitle1" sx={{ fontStyle: 'italic', mb: 1 }}>
                Mathematics and Computing, IIT (ISM) Dhanbad
              </Typography>
              <Typography variant="body2">Dec 2021 – Apr 2026</Typography>
            </Box>
          </motion.div>
        </Box>

        {/* Socials */}
        <Typography
          variant="h4"
          sx={{
            mt: 4,
            mb: 3,
            fontWeight: 'bold',
            textAlign: 'center',
            borderTop: `2px dotted ${darkMode ? '#757575' : '#bdbdbd'}`,
            pt: 3,
          }}
        >
          Find Me On
        </Typography>

        <Box sx={{ textAlign: 'center', mb: 6 }}>
          <IconButton
            href="https://github.com/yourusername"
            target="_blank"
            sx={{
              color: darkMode ? '#bdbdbd' : '#666666',
              '&:hover': {
                color: darkMode ? '#ffffff' : '#333333',
                transform: 'scale(1.2)',
              },
              m: 1,
            }}
          >
            <GitHubIcon fontSize="large" />
          </IconButton>
          <IconButton
            href="https://linkedin.com/in/yourusername"
            target="_blank"
            sx={{
              color: darkMode ? '#bdbdbd' : '#666666',
              '&:hover': {
                color: darkMode ? '#ffffff' : '#333333',
                transform: 'scale(1.2)',
              },
              m: 1,
            }}
          >
            <LinkedInIcon fontSize="large" />
          </IconButton>
          <IconButton
            href="https://twitter.com/yourusername"
            target="_blank"
            sx={{
              color: darkMode ? '#bdbdbd' : '#666666',
              '&:hover': {
                color: darkMode ? '#ffffff' : '#333333',
                transform: 'scale(1.2)',
              },
              m: 1,
            }}
          >
            <TwitterIcon fontSize="large" />
          </IconButton>
        </Box>
      </Container>
    </Box>
  );
};

export default Author;
