import React from 'react';
import {
  Box,
  Card,
  CardContent,
  Typography,
  Link,
  Avatar,
  useTheme,
} from '@mui/material';
import { motion } from 'framer-motion';

const sectionTitleStyle = {
  mb: 3,
  fontWeight: 'bold',
  color: '#673ab7',
};

export default function Author() {
  const theme = useTheme();
  const darkMode = theme.palette.mode === 'dark';

  const socialLinks = [
    { name: 'GitHub', url: 'https://github.com/subhamk-07' },
    { name: 'LinkedIn', url: 'https://linkedin.com/in/subhamk-07' },
    { name: 'Twitter', url: 'https://twitter.com/subhamk_07' },
  ];

  const techStack = [
    'C++', 'JavaScript', 'React', 'Node.js', 'MongoDB',
    'SQL', 'Python', 'Git', 'HTML/CSS', 'Express',
  ];

  const experiences = [
    {
      title: 'Web Developer Intern',
      company: 'XYZ Solutions',
      duration: 'May 2024 â€“ July 2024',
      description: 'Developed responsive web applications using React and Node.js. Contributed to backend APIs and optimized frontend performance.',
    },
    {
      title: 'Open Source Contributor',
      company: 'MetaMask, OpenMRS, CircuitVerse',
      duration: 'Ongoing',
      description: 'Contributed to major open-source repositories, fixing bugs, improving docs, and adding new features in React and JavaScript.',
    },
  ];

  return (
    <Card
      sx={{
        maxWidth: 700,
        mx: 'auto',
        mt: 6,
        mb: 8,
        boxShadow: 6,
        borderRadius: 4,
        bgcolor: darkMode ? '#1e1e1e' : '#fefefe',
        px: 3,
      }}
    >
      <CardContent>
        {/* === Profile Photo === */}
        <Box sx={{ display: 'flex', justifyContent: 'center', mb: 3 }}>
          <Avatar
            src="/profile.jpg" // <-- Make sure to place the photo in `public/profile.jpg`
            alt="Subham Kumar"
            sx={{ width: 100, height: 100 }}
          />
        </Box>

        {/* === About Me === */}
        <Typography variant="h4" sx={{ mb: 4, fontWeight: 'bold', color: '#512da8', textAlign: 'center' }}>
          About Me
        </Typography>

        <Typography variant="body1" sx={{ mb: 5, fontSize: 16 }}>
          Hi, I am Subham Kumar, a final-year dual degree student (B.Tech + M.Tech) in Mathematics and Computer Science at IIT (ISM) Dhanbad. Passionate about web and software development with experience in C++, JavaScript, React, and backend technologies.
        </Typography>

        {/* === Education === */}
        <Typography variant="h5" sx={sectionTitleStyle}>
          ðŸŽ“ Education
        </Typography>

        <motion.div initial={{ opacity: 0, x: -20 }} animate={{ opacity: 1, x: 0 }} transition={{ duration: 0.4 }}>
          <Box
            sx={{
              bgcolor: darkMode ? '#2c2c2c' : '#eeeeee',
              p: 3,
              borderRadius: 2,
              boxShadow: 3,
              mb: 6,
              borderLeft: `4px solid ${darkMode ? '#9575cd' : '#673ab7'}`,
            }}
          >
            <Typography variant="h6" sx={{ fontWeight: 'medium' }}>
              Integrated Master of Technology (B.Tech + M.Tech)
            </Typography>
            <Typography variant="subtitle1" sx={{ fontStyle: 'italic', mb: 1 }}>
              Mathematics and Computing, IIT (ISM) Dhanbad
            </Typography>
            <Typography variant="body2">Dec 2021 â€“ Apr 2026</Typography>
          </Box>
        </motion.div>

        {/* === Work Experience === */}
        <Typography variant="h5" sx={sectionTitleStyle}>
          ðŸ’¼ Work Experience
        </Typography>

        {experiences.map((exp, index) => (
          <motion.div
            key={index}
            initial={{ opacity: 0, x: -30 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.5, delay: index * 0.2 }}
            viewport={{ once: true }}
          >
            <Box
              sx={{
                bgcolor: darkMode ? '#2e2e2e' : '#f1f1f1',
                p: 3,
                borderRadius: 2,
                boxShadow: 2,
                mb: 4,
                borderLeft: `4px solid ${darkMode ? '#ba68c8' : '#7e57c2'}`,
              }}
            >
              <Typography variant="h6" sx={{ fontWeight: 'medium' }}>
                {exp.title} @ {exp.company}
              </Typography>
              <Typography variant="body2" sx={{ fontStyle: 'italic', mb: 1 }}>
                {exp.duration}
              </Typography>
              <Typography variant="body2">{exp.description}</Typography>
            </Box>
          </motion.div>
        ))}

        {/* === Tech Stack === */}
        <Typography variant="h5" sx={sectionTitleStyle}>
          ðŸ§° Tech Stack
        </Typography>

        <Box
          sx={{
            overflow: 'hidden',
            width: '100%',
            position: 'relative',
            mb: 6,
            height: '60px',
          }}
        >
          <motion.div
            animate={{
              x: ['100%', '-100%'],
            }}
            transition={{
              repeat: Infinity,
              duration: 18,
              ease: 'linear',
            }}
            style={{
              whiteSpace: 'nowrap',
              display: 'inline-block',
            }}
          >
            {techStack.map((tech, index) => (
              <Box
                key={index}
                sx={{
                  display: 'inline-block',
                  bgcolor: darkMode ? '#424242' : '#e0e0e0',
                  color: darkMode ? '#ffffff' : '#333',
                  px: 2,
                  py: 1,
                  mx: 1.5,
                  borderRadius: '20px',
                  fontWeight: 500,
                  fontSize: '14px',
                  boxShadow: 2,
                  minWidth: '80px',
                  textAlign: 'center',
                  userSelect: 'none',
                }}
              >
                {tech}
              </Box>
            ))}
          </motion.div>
        </Box>

        {/* === Social Links === */}
        <Typography variant="h5" sx={{ ...sectionTitleStyle, textAlign: 'center' }}>
          ðŸ”— Find Me On
        </Typography>

        <Box sx={{ display: 'flex', justifyContent: 'center', gap: 3, mt: 2, mb: 2 }}>
          {socialLinks.map(({ name, url }) => (
            <Link
              key={name}
              href={url}
              target="_blank"
              rel="noopener noreferrer"
              sx={{
                color: darkMode ? '#9575cd' : '#673ab7',
                fontWeight: 'medium',
                textDecoration: 'none',
                fontSize: '16px',
                '&:hover': {
                  textDecoration: 'underline',
                },
              }}
            >
              {name}
            </Link>
          ))}
        </Box>
      </CardContent>
    </Card>
  );
}
