                    <Typography variant="h6" component="div" sx={{ mb: 1, fontWeight: 'bold', color: darkMode ? '#ffffff' : '#333333' }}>Filters</Typography>
                    <Grid container spacing={1}>
                      <Grid item xs={12} sm={6} md={6}>
