                          <Select value={statusFilter} onChange={handleStatusChange} displayEmpty sx={{ bgcolor: darkMode ? '#121212' : '#ffffff', '.MuiOutlinedInput-notchedOutline': { borderColor: darkMode ? '#424242' : '#e0e0e0' }, '.MuiSelect-select': { color: darkMode ? '#ffffff' : '#333333' }, '.MuiSvgIcon-root': { color: darkMode ? '#ffffff' : '#333333' }, '&:hover .MuiOutlinedInput-notchedOutline': { borderColor: darkMode ? '#616161' : '#e0e0e0' }, '.Mui-focused .MuiOutlinedInput-notchedOutline': { borderColor: darkMode ? '#00aaff' : '#333333' } }} 
                            MenuProps={{
                              PaperProps: {
                                sx: {
                                  bgcolor: darkMode ? '#121212' : '#ffffff',
                                  color: darkMode ? '#ffffff' : '#333333',
                                  '& .MuiMenuItem-root': {
                                    color: darkMode ? '#ffffff' : '#333333',
                                    '&:hover': {
                                      bgcolor: darkMode ? '#424242' : '#f5f5f5',
                                    },
                                    '&.Mui-selected': {
                                      bgcolor: darkMode ? '#424242' : '#e0e0e0',
                                    },
                                  },
                                },
                              },
                            }}
                          >
                            <MenuItem value="all">All</MenuItem>
                            <MenuItem value="Completed">Completed</MenuItem>
                          </Select>
                        </FormControl>
                      </Grid>
                      <Grid item xs={122} sm={10} md={10}>
                        <Typography variant="caption" sx={{ display: 'block', mb: 0.5, fontWeight: 'medium', color: darkMode ? '#ffffff' : '#333333' }}>Difficulty</Typography>
                        <FormControl size="small" fullWidth>
                          <Select value={difficultyFilter} onChange={handleDifficultyChange} displayEmpty sx={{ bgcolor: darkMode ? '#121212' : '#ffffff', '.MuiOutlinedInput-notchedOutline': { borderColor: darkMode ? '#424242' : '#e0e0e0' }, '.MuiSelect-select': { color: darkMode ? '#ffffff' : '#333333' }, '.MuiSvgIcon-root': { color: darkMode ? '#ffffff' : '#333333' }, '&:hover .MuiOutlinedInput-notchedOutline': { borderColor: darkMode ? '#616161' : '#e0e0e0' }, '.Mui-focused .MuiOutlinedInput-notchedOutline': { borderColor: darkMode ? '#00aaff' : '#333333' } }}
                            MenuProps={{
                              PaperProps: {
                                sx: {
                                  bgcolor: darkMode ? '#121212' : '#ffffff',
                                  color: darkMode ? '#ffffff' : '#333333',
                                  '& .MuiMenuItem-root': {
                                    color: darkMode ? '#ffffff' : '#333333',
                                    '&:hover': {
                                      bgcolor: darkMode ? '#424242' : '#f5f5f5',
                                    },
                                    '&.Mui-selected': {
                                      bgcolor: darkMode ? '#424242' : '#e0e0e0',
                                    },
                                  },
                                },
                              },
                            }}
                          >
                            <MenuItem value="all">All</MenuItem>
                            <MenuItem value="Easy">Easy</MenuItem>
                            <MenuItem value="Medium">Medium</MenuItem>
                            <MenuItem value="Hard">Hard</MenuItem>
                          </Select>
                        </FormControl>
                      </Grid>
                      <Grid item xs={12} sm={6} md={6}>
                        <Typography variant="caption" sx={{ display: 'block', mb: 0.5, fontWeight: 'medium', color: darkMode ? '#ffffff' : '#333333' }}>Pattern</Typography>
                        <FormControl size="small" fullWidth>
                          <Select
                            value={patternFilter}
                            onChange={handlePatternChange}
                            displayEmpty
                             sx={{
                                 bgcolor: darkMode ? '#121212' : '#ffffff', 
                                 '.MuiOutlinedInput-notchedOutline': { borderColor: darkMode ? '#424242' : '#e0e0e0' }, 
                                 '.MuiSelect-select': { color: darkMode ? '#ffffff' : '#333333' }, '.MuiSvgIcon-root': { color: darkMode ? '#ffffff' : '#333333' },
                                 '&:hover .MuiOutlinedInput-notchedOutline': { borderColor: darkMode ? '#616161' : '#e0e0e0' },
                                 '.Mui-focused .MuiOutlinedInput-notchedOutline': { borderColor: darkMode ? '#00aaff' : '#333333' }
                               }}
                             MenuProps={{
                                 PaperProps: {
                                     sx: {
                                         maxHeight: 300,
                                         bgcolor: darkMode ? '#121212' : '#ffffff',
                                         color: darkMode ? '#ffffff' : '#333333',
                                         '& .MuiMenuItem-root': {
                                           color: darkMode ? '#ffffff' : '#333333',
                                           '&:hover': {
                                             bgcolor: darkMode ? '#424242' : '#f5f5f5',
                                           },
                                           '&.Mui-selected': {
                                             bgcolor: darkMode ? '#424242' : '#e0e0e0',
                                           },
                                         },
                                     },
                                 },
                                 anchorOrigin: {
                                     vertical: 'bottom',
                                     horizontal: 'left',
                                 },
                                 transformOrigin: {
                                     vertical: 'top',
                                     horizontal: 'left',
                                 },
                             }}
                           >
                             <Box sx={{ 
                               position: 'sticky', 
                               top: 0, 
                               bgcolor: darkMode ? '#121212' : '#ffffff',
                               borderBottom: `1px solid ${darkMode ? '#424242' : '#e0e0e0'}`,
                               p: 1,
                               zIndex: 1
                             }}>
                               <TextField
                                 size="small"
                                 placeholder="Search patterns..."
                                 value={patternSearchTerm}
                                 onChange={handlePatternSearchChange}
                                 fullWidth
                                 onClick={(e: React.MouseEvent) => e.stopPropagation()}
                                 sx={{
                                   bgcolor: darkMode ? '#121212' : '#ffffff',
                                   '.MuiOutlinedInput-notchedOutline': { borderColor: darkMode ? '#424242' : '#e0e0e0' },
                                   input: { color: darkMode ? '#ffffff' : '#333333' },
                                   '&:hover .MuiOutlinedInput-notchedOutline': { borderColor: darkMode ? '#616161' : '#e0e0e0' },
                                   '.Mui-focused .MuiOutlinedInput-notchedOutline': { borderColor: darkMode ? '#00aaff' : '#333333' }
                                 }}
                               />
                             </Box>
                             <MenuItem value="all">All Patterns</MenuItem>
                             <Box sx={{ 
                               borderTop: `1px solid ${darkMode ? '#424242' : '#e0e0e0'}`,
                               borderBottom: `1px solid ${darkMode ? '#424242' : '#e0e0e0'}`,
                               my: 1
                             }} />
                             {filteredPatternOptions.map(pattern => (
                                <MenuItem key={pattern.name} value={pattern.name}>{pattern.name}</MenuItem>
                             ))}
                           </Select>
                         </FormControl>
                       </Grid>
                       <Grid item xs={12} sm={6} md={6} sx={{ display: 'flex', alignItems: 'flex-end', gap: 1 }}>
                          <IconButton 
                            onClick={toggleStarredOnly} 
                            sx={{ color: showStarredOnly ? (darkMode ? '#ffeb3b' : '#ffc107') : (darkMode ? '#bdbdbd' : '#757575'), '&:hover': { color: showStarredOnly ? (darkMode ? '#ffc107' : '#ffeb3b') : (darkMode ? '#ffffff' : '#333333') } }} 
                          >
                              {showStarredOnly ? <StarIcon /> : <StarBorderIcon />}
                          </IconButton>
                          <Tooltip title="Reset Progress">
                             <IconButton 
                               onClick={handleOpenResetDialog}
                               sx={{ color: darkMode ? '#bdbdbd' : '#757575', '&:hover': { color: darkMode ? '#ffffff' : '#333333'} }}
                             >
                               <RefreshIcon />
                             </IconButton>
                          </Tooltip>
                       </Grid>
                    </Grid>
                  </Card>
                </Grid>

                <Grid item xs={12} md={2} sx={{ mb: 4 }}>
                  <Card elevation={3} sx={{ p: 2, bgcolor: darkMode ? '#212121' : '#ffffff', height: '100%', display: 'flex', flexDirection: 'column', justifyContent: 'space-between' }}>
                    <Typography variant="h6" component="div" sx={{ mb: 1, fontWeight: 'bold', color: darkMode ? '#ffffff' : '#333333' }}>Overall Progress</Typography>
                    <Box sx={{ display: 'flex', flexDirection: { xs: 'column', sm: 'row' }, alignItems: 'center', gap: { xs: 4, sm: 8 } }}>
                        <Box sx={{ position: 'relative', display: 'inline-flex', flexShrink: 0 }}>
                          <CircularProgress
                            variant="determinate"
                            value={overallProgress}
                            size={180}
                            thickness={5}
                            sx={{
                              color: darkMode ? '#66bb6a' : '#4caf50',
                              transition: 'stroke-dashoffset 0.3s ease 0s',
                               '& .MuiCircularProgress-circle': {
                                 stroke: darkMode ? '#424242' : '#e0e0e0',
                                 strokeLinecap: 'round',
