          <Toolbar>
            <Typography 
              variant="h6" 
              component="div" 
              sx={{ 
                flexGrow: 0, 
                fontWeight: 'bold', 
                color: darkMode ? '#ffffff' : '#333333', 
                letterSpacing: '0.1em',
                mr: 4
              }}>
