                    <Box display="flex" flexDirection="column" gap={1.5} sx={{ flexGrow: 1, width: { xs: '100%', md: 'auto' } }}>
                      <Box sx={{ bgcolor: darkMode ? '#333333' : '#eeeeee', p: 2, borderRadius: 1, textAlign: 'center', borderLeft: `4px solid #66bb6a` }}>
