                <DialogActions sx={{ bgcolor: darkMode ? '#212121' : '#ffffff' }}>
                  <Button onClick={handleCloseResetDialog} sx={{ color: darkMode ? '#bdbdbd' : '#666666' }}>Cancel</Button>
