import React from 'react';
import { Card, CardContent, Typography, LinearProgress, Box, IconButton, Link } from '@mui/material';
import { Pattern, Question } from '../data/patterns';
import StarIcon from '@mui/icons-material/Star';
import StarBorderIcon from '@mui/icons-material/StarBorder';
import CodeIcon from '@mui/icons-material/Code';
import LaunchIcon from '@mui/icons-material/Launch';
import CheckCircleOutlineIcon from '@mui/icons-material/CheckCircleOutline';
import PlayCircleOutlineIcon from '@mui/icons-material/PlayCircleOutline';
import RadioButtonUncheckedIcon from '@mui/icons-material/RadioButtonUnchecked'; // Icon for Not Started
import CircleOutlinedIcon from '@mui/icons-material/CircleOutlined'; // Icon for In Progress (using outlined circle)

interface PatternCardProps {
  pattern: Pattern;
  onToggleStar?: () => void; // This is for pattern-level star
  isStarred?: boolean; // This is for pattern-level star
  onQuestionStatusChange: (patternName: string, questionName: string, newStatus: Question['status']) => void;
  onToggleStarQuestion: (patternName: string, questionName: string) => void; // Handler for question-level star
  darkMode: boolean;
}

const PatternCard: React.FC<PatternCardProps> = ({ pattern, onToggleStar, isStarred, onQuestionStatusChange, onToggleStarQuestion, darkMode }) => {
  // Calculate progress based on completed questions
  const completedQuestions = pattern.questions.filter(q => q.status === 'Completed').length;
  const progress = (completedQuestions / pattern.questions.length) * 100;

  // Function to get difficulty color
  const getDifficultyColor = (difficulty: 'Easy' | 'Medium' | 'Hard') => {
    switch (difficulty) {
      case 'Easy':
        return '#66bb6a'; // Green
      case 'Medium':
        return '#ffa726'; // Orange
      case 'Hard':
        return '#ef5350'; // Red
      default:
        return '#e0e0e0'; // Default light gray
    }
  };

  const handleStatusClick = (question: Question) => {
    let newStatus: Question['status'];
    if (question.status === 'Not Started') {
      newStatus = 'In Progress';
    } else if (question.status === 'In Progress') {
      newStatus = 'Completed';
    } else {
      newStatus = 'Not Started';
    }
    onQuestionStatusChange(pattern.name, question.name, newStatus);
  };

  const handleQuestionStarClick = (questionName: string) => {
    onToggleStarQuestion(pattern.name, questionName);
  };

  // Function to get status icon with optimized rendering
  const getStatusIcon = (status: Question['status']) => {
    switch (status) {
      case 'Completed':
        return <CheckCircleOutlineIcon sx={{ color: '#66bb6a' }} fontSize="small" />;
      case 'In Progress':
        return <CircleOutlinedIcon sx={{ color: '#ffa726' }} fontSize="small" />;
      case 'Not Started':
      default:
        return <RadioButtonUncheckedIcon sx={{ color: '#bdbdbd' }} fontSize="small" />;
    }
  };

  // Memoize the status icon to prevent unnecessary re-renders
  const StatusIcon = React.memo(({ status }: { status: Question['status'] }) => getStatusIcon(status));

  return (
    <Card 
      sx={{
        mb: 2,
        position: 'relative',
        bgcolor: darkMode ? '#262626' : '#ffffff',
        color: darkMode ? '#e0e0e0' : '#333333',
        boxShadow: 3,
        borderRadius: 2,
        width: '100%',
        mx: 'auto'
      }}
    >
      <CardContent sx={{ p: { xs: 2, sm: 3, md: 4 } }}>
        {/* Header with Pattern Name, Link, and Star */}
        <Box display="flex" justifyContent="space-between" alignItems="center" sx={{ mb: 2 }}>
          <Box display="flex" alignItems="center">
            <Typography variant="h5" component="div" sx={{ mr: 1, fontWeight: 'bold', color: darkMode ? '#ffffff' : '#333333' }}>
              {pattern.name}
            </Typography>
            {/* Placeholder link for pattern */}
            <Link href="#" target="_blank" rel="noopener noreferrer" color="inherit" sx={{ lineHeight: 1, color: darkMode ? '#bdbdbd' : '#666666' }}>
              <LaunchIcon fontSize="small" />
            </Link>
          </Box>

          <IconButton onClick={onToggleStar} size="small" sx={{ color: isStarred ? '#ffeb3b' : darkMode ? '#bdbdbd' : '#666666' }}>
            {isStarred ? <StarIcon /> : <StarBorderIcon />}
          </IconButton>
        </Box>

        {/* Progress Bar */}
        <Typography variant="body2" sx={{ mb: 1, fontWeight: 'medium', color: darkMode ? '#ffffff' : '#333333' }}>
          Progress: {completedQuestions} / {pattern.questions.length}
        </Typography>
        <LinearProgress 
          variant="determinate" 
          value={progress} 
          sx={{
            height: 8,
            borderRadius: 4,
            bgcolor: darkMode ? '#424242' : '#e0e0e0',
            '& .MuiLinearProgress-bar': { 
              bgcolor: '#66bb6a',
              transition: 'transform 0.2s ease-in-out'
            }
          }}
        />
        
        {/* Table Header */}
        <Box sx={{ mt: 4, mb: 1.5, display: 'flex', justifyContent: 'space-between', pb: 1, borderBottom: `1px solid ${darkMode ? '#424242' : '#e0e0e0'}` }}>
          <Typography variant="caption" sx={{ width: '10%', fontWeight: 'bold', color: darkMode ? '#ffffff' : '#333333', textAlign: 'center' }}>Status</Typography>
          <Typography variant="caption" sx={{ width: '40%', fontWeight: 'bold', color: darkMode ? '#ffffff' : '#333333' }}>Problem</Typography>
          <Typography variant="caption" sx={{ width: '20%', fontWeight: 'bold', color: darkMode ? '#ffffff' : '#333333', textAlign: 'center' }}>Difficulty</Typography>
          <Typography variant="caption" sx={{ width: '20%', fontWeight: 'bold', color: darkMode ? '#ffffff' : '#333333', textAlign: 'center' }}>Solution</Typography>
          <Typography variant="caption" sx={{ width: '10%', fontWeight: 'bold', color: darkMode ? '#ffffff' : '#333333', textAlign: 'center' }}>Star</Typography>
        </Box>

        {/* Questions List */}
        <Box>
          {pattern.questions.map((question, index) => (
            <Box 
              key={index} 
              sx={{
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center',
                py: 1.5,
                borderBottom: index === pattern.questions.length - 1 ? 'none' : `1px solid ${darkMode ? '#333333' : '#e0e0e0'}`,
                '&:hover': { bgcolor: darkMode ? '#2c2c2c' : '#f5f5f5' },
                transition: 'background-color 0.2s ease-in-out'
              }}
            >
              {/* Status */}
              <Box sx={{ width: '10%', display: 'flex', justifyContent: 'center' }}>
                 <IconButton 
                   size="small" 
                   onClick={() => handleStatusClick(question)} 
                   sx={{ 
                     color: getDifficultyColor(question.difficulty),
                     transition: 'transform 0.1s ease-in-out',
                     '&:active': {
                       transform: 'scale(0.9)'
                     }
                   }}
                 >
                    <StatusIcon status={question.status} />
                 </IconButton>
              </Box>
              
              {/* Problem */}
              <Box sx={{ width: '40%', display: 'flex', alignItems: 'center' }}>
                <Link 
                  href="#" 
                  target="_blank" 
                  rel="noopener noreferrer"
                  color="inherit"
                  underline="none"
                  sx={{ 
                    '&:hover': { textDecoration: 'underline' },
                    mr: 0.5,
                    color: '#90caf9', // Brighter link color
                    flexGrow: 1 // Allow problem name to take available space
                   }}
                >
                  {question.name}
                </Link>
                <LaunchIcon sx={{ fontSize: 12, color: '#757575' }} />
              </Box>

              {/* Difficulty */}
              <Typography 
                variant="body2" 
                sx={{
                  width: '20%',
                  color: getDifficultyColor(question.difficulty),
                  fontWeight: 'bold',
                   textAlign: 'center'
                }}
              >
                {question.difficulty}
              </Typography>
              
              {/* Solution */}
              <Box sx={{ width: '20%', display: 'flex', gap: 1, justifyContent: 'center' }}>
                 {/* GitHub Link */}
                 {question.solution && (
                   <Link href={question.solution} target="_blank" rel="noopener noreferrer" color="inherit" sx={{ lineHeight: 1, color: '#90caf9' }}>
                      <CodeIcon fontSize="small" />
                   </Link>
                 )}
                 {/* YouTube Link Placeholder */}
                 <Link href="#" target="_blank" rel="noopener noreferrer" color="inherit" sx={{ lineHeight: 1, color: '#90caf9' }}>
                    <PlayCircleOutlineIcon fontSize="small" />
                 </Link>
              </Box>

              {/* Star (Question level) */}
              <Box sx={{ width: '10%', textAlign: 'center' }}>
                 <IconButton size="small" onClick={() => handleQuestionStarClick(question.name)} sx={{ color: question.starred ? '#ffeb3b' : '#bdbdbd' }}>
                   {question.starred ? <StarIcon fontSize="small" /> : <StarBorderIcon fontSize="small" />}
                 </IconButton>
              </Box>

            </Box>
          ))}
        </Box>
      </CardContent>
    </Card>
  );
};

export default PatternCard;
