import React, { useState, useEffect } from 'react';
import {
  Container,
  Typography,
  TextField,
  Box,
  Grid,
  AppBar,
  Toolbar,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  SelectChangeEvent,
  Link,
  IconButton,
  LinearProgress
} from '@mui/material';
import GitHubIcon from '@mui/icons-material/GitHub';
import YouTubeIcon from '@mui/icons-material/YouTube';
import PersonIcon from '@mui/icons-material/Person';
import MenuIcon from '@mui/icons-material/Menu';
import SearchIcon from '@mui/icons-material/Search';
import StarBorderIcon from '@mui/icons-material/StarBorder';
import StarIcon from '@mui/icons-material/Star';
import RefreshIcon from '@mui/icons-material/Refresh';
import DoneAllIcon from '@mui/icons-material/DoneAll';

import { patterns as initialPatterns, Pattern, Question } from './data/patterns';
import PatternCard from './components/PatternCard';

const LOCAL_STORAGE_KEY = 'dsaPatternsProgress';

function App() {
  const [patterns, setPatterns] = useState<Pattern[]>(() => {
    const savedPatterns = localStorage.getItem(LOCAL_STORAGE_KEY);
    return savedPatterns ? JSON.parse(savedPatterns) : initialPatterns;
  });
  const [searchTerm, setSearchTerm] = useState('');
  const [difficultyFilter, setDifficultyFilter] = useState<string>('all');
  const [statusFilter, setStatusFilter] = useState<string>('all');
  const [patternFilter, setPatternFilter] = useState<string>('all');
  const [languageFilter, setLanguageFilter] = useState<string>('all');
  const [showStarredOnly, setShowStarredOnly] = useState(false);
  const [starredPatterns, setStarredPatterns] = useState<Set<string>>(new Set());
  const [overallProgress, setOverallProgress] = useState(0);
  const [difficultyProgress, setDifficultyProgress] = useState({
    easy: { completed: 0, total: 0, percentage: 0 },
    medium: { completed: 0, total: 0, percentage: 0 },
    hard: { completed: 0, total: 0, percentage: 0 },
  });

  useEffect(() => {
    localStorage.setItem(LOCAL_STORAGE_KEY, JSON.stringify(patterns));
    let totalQuestions = 0;
    let completedQuestions = 0;
    let easyCompleted = 0, easyTotal = 0;
    let mediumCompleted = 0, mediumTotal = 0;
    let hardCompleted = 0, hardTotal = 0;

    patterns.forEach(pattern => {
      totalQuestions += pattern.questions.length;
      completedQuestions += pattern.questions.filter(q => q.status === 'Completed').length;

      pattern.questions.forEach(question => {
        if (question.difficulty === 'Easy') {
          easyTotal++;
          if (question.status === 'Completed') easyCompleted++;
        } else if (question.difficulty === 'Medium') {
          mediumTotal++;
          if (question.status === 'Completed') mediumCompleted++;
        } else if (question.difficulty === 'Hard') {
          hardTotal++;
          if (question.status === 'Completed') hardCompleted++;
        }
      });
    });

    setOverallProgress(totalQuestions === 0 ? 0 : (completedQuestions / totalQuestions) * 100);
    setDifficultyProgress({
      easy: { completed: easyCompleted, total: easyTotal, percentage: easyTotal === 0 ? 0 : (easyCompleted / easyTotal) * 100 },
      medium: { completed: mediumCompleted, total: mediumTotal, percentage: mediumTotal === 0 ? 0 : (mediumCompleted / mediumTotal) * 100 },
      hard: { completed: hardCompleted, total: hardTotal, percentage: hardTotal === 0 ? 0 : (hardCompleted / hardTotal) * 100 },
    });
  }, [patterns]);

  const handleSearchChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setSearchTerm(event.target.value);
  };

  const handleDifficultyChange = (event: SelectChangeEvent) => {
    setDifficultyFilter(event.target.value);
  };

  const handleStatusChange = (event: SelectChangeEvent) => {
    setStatusFilter(event.target.value);
  };

  const handlePatternChange = (event: SelectChangeEvent) => {
    setPatternFilter(event.target.value);
  };

  const handleLanguageChange = (event: SelectChangeEvent) => {
    setLanguageFilter(event.target.value);
  };

  const toggleStarredOnly = () => {
    setShowStarredOnly(prev => !prev);
  };

  const toggleStarPattern = (patternName: string) => {
    setStarredPatterns(prev => {
      const newSet = new Set(prev);
      if (newSet.has(patternName)) {
        newSet.delete(patternName);
      } else {
        newSet.add(patternName);
      }
      return newSet;
    });
  };

  const handleQuestionStatusChange = (patternName: string, questionName: string, newStatus: Question['status']) => {
    setPatterns(prevPatterns => {
      const updatedPatterns = prevPatterns.map(pattern => {
        if (pattern.name === patternName) {
          const updatedQuestions = pattern.questions.map(question => {
            if (question.name === questionName) {
              return { ...question, status: newStatus };
            }
            return question;
          });

          // Calculate new progress
          const completedQuestions = updatedQuestions.filter(q => q.status === 'Completed').length;
          return { 
            ...pattern, 
            questions: updatedQuestions,
            progress: {
              completed: completedQuestions,
              total: updatedQuestions.length
            }
          };
        }
        return pattern;
      });
      return updatedPatterns;
    });
  };

  const toggleStarQuestion = (patternName: string, questionName: string) => {
    setPatterns(prevPatterns => {
      return prevPatterns.map(pattern => {
        if (pattern.name === patternName) {
          const updatedQuestions = pattern.questions.map(question => {
            if (question.name === questionName) {
              return { ...question, starred: !question.starred };
            }
            return question;
          });
          return { ...pattern, questions: updatedQuestions };
        }
        return pattern;
      });
    });
  };

  const filteredPatterns = patterns.filter(pattern => {
    const matchesPattern = patternFilter === 'all' || pattern.name === patternFilter;

    const questionsMatchFilters = pattern.questions.filter(question => {
      const matchesSearch = searchTerm === '' || question.name.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesDifficulty = difficultyFilter === 'all' || question.difficulty === difficultyFilter;
      const matchesStatus = statusFilter === 'all' || question.status === statusFilter;
      const matchesStarred = !showStarredOnly || question.starred;

      return matchesSearch && matchesDifficulty && matchesStatus && matchesStarred;
    });

    return matchesPattern && questionsMatchFilters.length > 0;
  }).map(pattern => {
    const questionsMatchFilters = pattern.questions.filter(question => {
      const matchesSearch = searchTerm === '' || question.name.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesDifficulty = difficultyFilter === 'all' || question.difficulty === difficultyFilter;
      const matchesStatus = statusFilter === 'all' || question.status === statusFilter;
      const matchesStarred = !showStarredOnly || question.starred;
      return matchesSearch && matchesDifficulty && matchesStatus && matchesStarred;
    });
    return { ...pattern, questions: questionsMatchFilters };
  });

  return (
    <Box sx={{ flexGrow: 1, bgcolor: '#1a1a1a', minHeight: '100vh', color: '#e0e0e0' }}>
      <AppBar position="static" sx={{ bgcolor: '#262626', boxShadow: 3 }}>
        <Toolbar>
          <Typography 
            variant="h6" 
            component="div" 
            sx={{ 
              flexGrow: 1, 
              fontWeight: 'bold', 
              color: '#ffffff', 
              letterSpacing: '0.1em' 
            }}>
            Algo<Box component="span" sx={{ color: '#00aaff' }}>Master</Box>
          </Typography>

          <Box sx={{ display: { xs: 'none', md: 'flex' }, alignItems: 'center', mr: 3, gap: 3 }}>
            <Link href="#" color="inherit" underline="none" sx={{ color: '#bdbdbd', fontWeight: 'medium', '&:hover': { color: '#ffffff' } }}>Learn</Link>
            <Link href="#" color="inherit" underline="none" sx={{ color: '#bdbdbd', fontWeight: 'medium', '&:hover': { color: '#ffffff' } }}>Practice</Link>
            <Link href="#" color="inherit" underline="none" sx={{ color: '#bdbdbd', fontWeight: 'medium', '&:hover': { color: '#ffffff' } }}>Newsletter</Link>
          </Box>

          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1.5 }}>
             <IconButton color="inherit" href="#" target="_blank" sx={{ color: '#bdbdbd', '&:hover': { color: '#ffffff' } }}><YouTubeIcon fontSize="small"/></IconButton>
             <IconButton color="inherit" href="#" target="_blank" sx={{ color: '#bdbdbd', '&:hover': { color: '#ffffff' } }}><GitHubIcon fontSize="small"/></IconButton>
             <IconButton color="inherit" sx={{ color: '#bdbdbd', '&:hover': { color: '#ffffff' } }}><PersonIcon fontSize="small"/></IconButton>
             <IconButton color="inherit" sx={{ display: { xs: 'block', md: 'none' }, color: '#bdbdbd', '&:hover': { color: '#ffffff' } }}><MenuIcon fontSize="small"/></IconButton>
          </Box>
        </Toolbar>
      </AppBar>

      <Container maxWidth={false} sx={{ mt: 4, mb: 4, px: { xs: 2, sm: 3, md: 4 } }}>
         <Box 
           sx={{
             bgcolor: '#212121',
             p: 3,
             borderRadius: 2,
             boxShadow: 1,
             mb: 4
           }}
         >
            <Typography variant="h6" component="div" sx={{ mb: 2, fontWeight: 'bold', color: '#ffffff' }}>Filters</Typography>
            <Grid container spacing={2}>
                <Grid item xs={12} sm={6} md={3}>
                    <Typography variant="caption" sx={{ display: 'block', mb: 0.5, fontWeight: 'medium', color: '#bdbdbd' }}>Search</Typography>
                    <TextField
                      size="small"
                      placeholder="Search problems..."
                      value={searchTerm}
                      onChange={handleSearchChange}
                      fullWidth
                      InputProps={{
                        startAdornment: (
                          <SearchIcon sx={{ color: '#bdbdbd', mr: 1 }} />
                        ),
                      }}
                      sx={{
                        bgcolor: '#121212',
                        '.MuiOutlinedInput-notchedOutline': { borderColor: '#424242' }, 
                        input: { color: '#e0e0e0' }, 
                        label: { color: '#bdbdbd' },
                        '&:hover .MuiOutlinedInput-notchedOutline': { borderColor: '#616161' },
                        '.Mui-focused .MuiOutlinedInput-notchedOutline': { borderColor: '#00aaff' }
                      }}
                      InputLabelProps={{ style: { color: '#bdbdbd' } }}
                    />
                </Grid>
                <Grid item xs={12} sm={6} md={3}>
                    <Typography variant="caption" sx={{ display: 'block', mb: 0.5, fontWeight: 'medium', color: '#bdbdbd' }}>Status</Typography>
                    <FormControl size="small" fullWidth>
                       <Select
                          value={statusFilter}
                          onChange={handleStatusChange}
                          displayEmpty
                           sx={{
                              bgcolor: '#121212', 
                              '.MuiOutlinedInput-notchedOutline': { borderColor: '#424242' }, 
                              '.MuiSelect-select': { color: '#e0e0e0' }, 
                              '.MuiSvgIcon-root': { color: '#e0e0e0' },
                              '&:hover .MuiOutlinedInput-notchedOutline': { borderColor: '#616161' },
                              '.Mui-focused .MuiOutlinedInput-notchedOutline': { borderColor: '#00aaff' }
                            }}
                        >
                          <MenuItem value="all">All</MenuItem>
                          <MenuItem value="Not Started">Not Started</MenuItem>
                          <MenuItem value="In Progress">In Progress</MenuItem>
                          <MenuItem value="Completed">Completed</MenuItem>
                        </Select>
                    </FormControl>
                </Grid>
                 <Grid item xs={12} sm={6} md={3}>
                     <Typography variant="caption" sx={{ display: 'block', mb: 0.5, fontWeight: 'medium', color: '#bdbdbd' }}>Difficulty</Typography>
                     <FormControl size="small" fullWidth>
                       <Select
                         value={difficultyFilter}
                         onChange={handleDifficultyChange}
                         displayEmpty
                          sx={{
                              bgcolor: '#121212', 
                              '.MuiOutlinedInput-notchedOutline': { borderColor: '#424242' }, 
                              '.MuiSelect-select': { color: '#e0e0e0' }, 
                              '.MuiSvgIcon-root': { color: '#e0e0e0' },
                              '&:hover .MuiOutlinedInput-notchedOutline': { borderColor: '#616161' },
                              '.Mui-focused .MuiOutlinedInput-notchedOutline': { borderColor: '#00aaff' }
                            }}
                       >
                         <MenuItem value="all">All</MenuItem>
                         <MenuItem value="Easy">Easy</MenuItem>
                         <MenuItem value="Medium">Medium</MenuItem>
                         <MenuItem value="Hard">Hard</MenuItem>
                       </Select>
                     </FormControl>
                 </Grid>
                 <Grid item xs={12} sm={6} md={3}>
                     <Typography variant="caption" sx={{ display: 'block', mb: 0.5, fontWeight: 'medium', color: '#bdbdbd' }}>Pattern</Typography>
                     <FormControl size="small" fullWidth>
                       <Select
                         value={patternFilter}
                         onChange={handlePatternChange}
                         displayEmpty
                          sx={{
                              bgcolor: '#121212', 
                              '.MuiOutlinedInput-notchedOutline': { borderColor: '#424242' }, 
                              '.MuiSelect-select': { color: '#e0e0e0' }, 
                              '.MuiSvgIcon-root': { color: '#e0e0e0' },
                              '&:hover .MuiOutlinedInput-notchedOutline': { borderColor: '#616161' },
                              '.Mui-focused .MuiOutlinedInput-notchedOutline': { borderColor: '#00aaff' }
                            }}
                       >
                         <MenuItem value="all">All Patterns</MenuItem>
                         {initialPatterns.map(pattern => (
                            <MenuItem key={pattern.name} value={pattern.name}>{pattern.name}</MenuItem>
                         ))}
                       </Select>
                     </FormControl>
                 </Grid>
                 <Grid item xs={12} sm={6} md={3}>
                     <Typography variant="caption" sx={{ display: 'block', mb: 0.5, fontWeight: 'medium', color: '#bdbdbd' }}>Preferred Language</Typography>
                     <FormControl size="small" fullWidth>
                       <Select
                         value={languageFilter}
                         onChange={handleLanguageChange}
                         displayEmpty
                          sx={{
                              bgcolor: '#121212', 
                              '.MuiOutlinedInput-notchedOutline': { borderColor: '#424242' }, 
                              '.MuiSelect-select': { color: '#e0e0e0' }, 
                              '.MuiSvgIcon-root': { color: '#e0e0e0' },
                              '&:hover .MuiOutlinedInput-notchedOutline': { borderColor: '#616161' },
                              '.Mui-focused .MuiOutlinedInput-notchedOutline': { borderColor: '#00aaff' }
                            }}
                       >
                         <MenuItem value="all">All Languages</MenuItem>
                         <MenuItem value="cpp">C++</MenuItem>
                         <MenuItem value="python">Python</MenuItem>
                       </Select>
                     </FormControl>
                 </Grid>
                 <Grid item xs={12} sm={6} md={3} sx={{ display: 'flex', alignItems: 'flex-end', gap: 1 }}>
                    <Typography variant="caption" sx={{ display: 'block', mb: 0.5, fontWeight: 'medium', color: '#bdbdbd' }}>Starred Only</Typography>
                    <IconButton 
                      onClick={toggleStarredOnly} 
                      sx={{ color: showStarredOnly ? '#ffeb3b' : '#bdbdbd', '&:hover': { color: showStarredOnly ? '#ffc107' : '#ffffff' } }}
                    >
                        {showStarredOnly ? <StarIcon /> : <StarBorderIcon />}
                    </IconButton>
                    <IconButton sx={{ color: '#bdbdbd', '&:hover': { color: '#ffffff' } }}><RefreshIcon /></IconButton>
                 </Grid>
            </Grid>
         </Box>

         <Box sx={{ width: '100%', mb: 4 }}>
            <Typography variant="h6" component="div" sx={{ mb: 1, fontWeight: 'bold', color: '#ffffff' }}>Overall Progress</Typography>
            <LinearProgress 
               variant="determinate" 
               value={overallProgress} 
               sx={{
                 height: 10,
                 borderRadius: 5,
                 bgcolor: '#424242',
                 '& .MuiLinearProgress-bar': { bgcolor: '#66bb6a' }
               }}
            />
             <Typography variant="body2" color="text.secondary" sx={{ mt: 1, textAlign: 'center' }}>
               {overallProgress.toFixed(0)}% Completed
            </Typography>
         </Box>

        <Box sx={{ width: '100%', mb: 4 }}>
          <Typography variant="h6" component="div" sx={{ mb: 1, fontWeight: 'bold', color: '#ffffff' }}>Progress by Difficulty</Typography>
          <Box sx={{ mb: 2 }}>
            <Typography variant="body2" color="text.secondary" sx={{ mb: 0.5 }}>Easy: {difficultyProgress.easy.completed} / {difficultyProgress.easy.total} ({difficultyProgress.easy.percentage.toFixed(0)}%)</Typography>
            <LinearProgress variant="determinate" value={difficultyProgress.easy.percentage} sx={{ height: 8, borderRadius: 4, bgcolor: '#424242', '& .MuiLinearProgress-bar': { bgcolor: '#66bb6a' } }} />
          </Box>
          <Box sx={{ mb: 2 }}>
            <Typography variant="body2" color="text.secondary" sx={{ mb: 0.5 }}>Medium: {difficultyProgress.medium.completed} / {difficultyProgress.medium.total} ({difficultyProgress.medium.percentage.toFixed(0)}%)</Typography>
            <LinearProgress variant="determinate" value={difficultyProgress.medium.percentage} sx={{ height: 8, borderRadius: 4, bgcolor: '#424242', '& .MuiLinearProgress-bar': { bgcolor: '#ffa726' } }} />
          </Box>
          <Box>
            <Typography variant="body2" color="text.secondary" sx={{ mb: 0.5 }}>Hard: {difficultyProgress.hard.completed} / {difficultyProgress.hard.total} ({difficultyProgress.hard.percentage.toFixed(0)}%)</Typography>
            <LinearProgress variant="determinate" value={difficultyProgress.hard.percentage} sx={{ height: 8, borderRadius: 4, bgcolor: '#424242', '& .MuiLinearProgress-bar': { bgcolor: '#ef5350' } }} />
          </Box>
        </Box>

        <Grid container spacing={4}>
          {filteredPatterns.map((pattern) => (
            <Grid item xs={12} key={pattern.name} sx={{ width: '100%' }}>
              <PatternCard
                pattern={pattern}
                isStarred={starredPatterns.has(pattern.name)}
                onToggleStar={() => toggleStarPattern(pattern.name)}
                onQuestionStatusChange={handleQuestionStatusChange}
                onToggleStarQuestion={toggleStarQuestion}
              />
            </Grid>
          ))}
        </Grid>
      </Container>
    </Box>
  );
}

export default App;
