import React from 'react';
import { Box, Container, Typography, Card, CardContent } from '@mui/material';

interface CompaniesProps {
  darkMode: boolean;
}

const companies = [
  'Google',
  'Amazon',
  'Netflix',
  'Meta',
  'Microsoft'
];

const Companies: React.FC<CompaniesProps> = ({ darkMode }) => {
  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Typography 
        variant="h4" 
        component="h1" 
        sx={{ 
          mb: 4, 
          textAlign: 'center',
          color: darkMode ? '#ffffff' : '#333333',
          fontWeight: 'bold'
        }}
      >
        Top Tech Companies
      </Typography>
      
      <Box sx={{ 
        display: 'grid',
        gridTemplateColumns: {
          xs: '1fr',
          sm: 'repeat(2, 1fr)',
          md: 'repeat(3, 1fr)'
        },
        gap: 3
      }}>
        {companies.map((company) => (
          <Card 
            key={company}
            sx={{ 
              height: '100%',
              display: 'flex',
              flexDirection: 'column',
              bgcolor: darkMode ? '#262626' : '#ffffff',
              transition: 'transform 0.2s',
              '&:hover': {
                transform: 'scale(1.02)',
                boxShadow: 6
              }
            }}
          >
            <CardContent>
              <Typography 
                variant="h5" 
                component="div"
                sx={{ 
                  color: darkMode ? '#ffffff' : '#333333',
                  fontWeight: 'bold',
                  textAlign: 'center'
                }}
              >
                {company}
              </Typography>
            </CardContent>
          </Card>
        ))}
      </Box>
    </Container>
  );
};

export default Companies;
