import React, { useEffect, useRef } from 'react';
import { motion } from 'framer-motion';
import { useSpring, animated } from 'react-spring';
import gsap from 'gsap';
import { Box, Typography, Container, Grid, IconButton, styled } from '@mui/material';
import GitHubIcon from '@mui/icons-material/GitHub';
import LinkedInIcon from '@mui/icons-material/LinkedIn';
import TwitterIcon from '@mui/icons-material/Twitter';

const StyledContainer = styled(Container)(({ theme }) => ({
  minHeight: '100vh',
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  padding: theme.spacing(4),
}));

const ProfileImage = styled(motion.img)({
  width: '200px',
  height: '200px',
  borderRadius: '50%',
  objectFit: 'cover',
  marginBottom: '2rem',
});

const SocialButton = styled(IconButton)(({ theme }) => ({
  margin: theme.spacing(1),
  transition: 'transform 0.2s',
  '&:hover': {
    transform: 'scale(1.1)',
  },
}));

const ExperienceCard = styled(motion.div)(({ theme }) => ({
  background: theme.palette.mode === 'dark' ? '#1a1a1a' : '#ffffff',
  padding: theme.spacing(3),
  borderRadius: theme.spacing(2),
  boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
  marginBottom: theme.spacing(2),
}));

const Author: React.FC = () => {
  const titleRef = useRef(null);
  const contentRef = useRef(null);

  useEffect(() => {
    gsap.from(titleRef.current, {
      duration: 1,
      y: -50,
      opacity: 0,
      ease: 'power3.out',
    });

    gsap.from(contentRef.current, {
      duration: 1,
      y: 50,
      opacity: 0,
      delay: 0.5,
      ease: 'power3.out',
    });
  }, []);

  const fadeIn = useSpring({
    from: { opacity: 0 },
    to: { opacity: 1 },
    config: { duration: 1000 },
  });

  return (
    <StyledContainer>
      <animated.div style={fadeIn}>
        <Box ref={titleRef} sx={{ textAlign: 'center', mb: 6 }}>
          <ProfileImage
            src="https://drive.google.com/uc?export=view&id=13BT-0z_rbo7Vje1COpEL98rh5xDYiDY4"
            alt="Subham Kumar"
            initial={{ scale: 0 }}
            animate={{ scale: 1 }}
            transition={{ duration: 0.5 }}
          />
          <Typography variant="h3" component="h1" gutterBottom>
            Hi there! I'm Subham.
          </Typography>
          <Typography variant="h6" color="textSecondary" paragraph>
            I started building on the web in 2007 and never stopped.
          </Typography>
        </Box>

        <Box ref={contentRef}>
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.2 }}
          >
            <Typography variant="body1" paragraph>
              I've worked in senior engineering positions at organizations like Gatsby (acq. Netlify), DigitalOcean, and Khan Academy. I've also taught web development fundamentals at Concordia University.
            </Typography>
            <Typography variant="body1" paragraph>
              These days, I'm a solo indie hacker focused on empowering up-and-coming software developers.
            </Typography>
          </motion.div>

          <Typography variant="h4" sx={{ mt: 6, mb: 3 }}>Work Experience</Typography>
          <Grid container spacing={3}>
            <Grid item xs={12} md={6}>
              <ExperienceCard
                whileHover={{ scale: 1.02 }}
                initial={{ opacity: 0, x: -50 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ delay: 0.3 }}
              >
                <Typography variant="h6">Frontend Engineer Intern</Typography>
                <Typography variant="subtitle1" color="textSecondary">Sportthon</Typography>
                <Typography variant="body2">Jan 2025 - March 2025</Typography>
                <Typography variant="body2">React, CSS, RestAPI, MySQL</Typography>
              </ExperienceCard>
            </Grid>
            <Grid item xs={12} md={6}>
              <ExperienceCard
                whileHover={{ scale: 1.02 }}
                initial={{ opacity: 0, x: 50 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ delay: 0.4 }}
              >
                <Typography variant="h6">Open Source Contributor</Typography>
                <Typography variant="subtitle1" color="textSecondary">Dec 2023 - April 2024</Typography>
                <Typography variant="body2">Metamask, Circuitverse, OpenMRS</Typography>
              </ExperienceCard>
            </Grid>
            <Grid item xs={12} md={6}>
              <ExperienceCard
                whileHover={{ scale: 1.02 }}
                initial={{ opacity: 0, x: -50 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ delay: 0.5 }}
              >
                <Typography variant="h6">Open Source Mentor</Typography>
                <Typography variant="subtitle1" color="textSecondary">Circuitverse</Typography>
                <Typography variant="body2">May 2025 - August 2025</Typography>
              </ExperienceCard>
            </Grid>
          </Grid>

          <Typography variant="h4" sx={{ mt: 6, mb: 3 }}>Education</Typography>
          <ExperienceCard
            whileHover={{ scale: 1.02 }}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.6 }}
          >
            <Typography variant="h6">Integrated Master of Technology</Typography>
            <Typography variant="subtitle1" color="textSecondary">B.Tech + M.Tech in Mathematics and Computing</Typography>
            <Typography variant="body2">Dec 2021 - April 2026</Typography>
          </ExperienceCard>

          <Box sx={{ mt: 6, textAlign: 'center' }}>
            <SocialButton href="https://github.com/yourusername" target="_blank">
              <GitHubIcon />
            </SocialButton>
            <SocialButton href="https://linkedin.com/in/yourusername" target="_blank">
              <LinkedInIcon />
            </SocialButton>
            <SocialButton href="https://twitter.com/yourusername" target="_blank">
              <TwitterIcon />
            </SocialButton>
          </Box>
        </Box>
      </animated.div>
    </StyledContainer>
  );
};

export default Author;
