import React from 'react';
import { Card, CardContent, Typography, LinearProgress, Box, IconButton, Link } from '@mui/material';
import { Pattern } from '../data/patterns';
import StarIcon from '@mui/icons-material/Star';
import StarBorderIcon from '@mui/icons-material/StarBorder';
import CodeIcon from '@mui/icons-material/Code';

interface PatternCardProps {
  pattern: Pattern;
  onToggleStar?: () => void;
  isStarred?: boolean;
}

const PatternCard: React.FC<PatternCardProps> = ({ pattern, onToggleStar, isStarred }) => {
  const progress = (pattern.progress.completed / pattern.progress.total) * 100;

  return (
    <Card sx={{ minWidth: 275, mb: 2, position: 'relative' }}>
      <CardContent>
        <Box display="flex" justifyContent="space-between" alignItems="center">
          <Typography variant="h5" component="div">
            {pattern.name}
          </Typography>
          <IconButton onClick={onToggleStar} size="small">
            {isStarred ? <StarIcon color="primary" /> : <StarBorderIcon />}
          </IconButton>
        </Box>
        <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
          Progress: {pattern.progress.completed} / {pattern.progress.total}
        </Typography>
        <LinearProgress 
          variant="determinate" 
          value={progress} 
          sx={{ mt: 1, height: 8, borderRadius: 4 }}
        />
        <Box sx={{ mt: 2 }}>
          {pattern.questions.map((question, index) => (
            <Box 
              key={index} 
              sx={{ 
                display: 'flex', 
                justifyContent: 'space-between',
                alignItems: 'center',
                mb: 0.5,
              }}
            >
              <Typography 
                variant="body2" 
                sx={{ 
                  color: question.status === 'Completed' ? 'success.main' : 'text.primary',
                  flex: 1
                }}
              >
                {question.name}
              </Typography>
              <Typography 
                variant="body2" 
                sx={{ 
                  color: 
                    question.difficulty === 'Easy' ? '#4caf50' : 
                    question.difficulty === 'Medium' ? '#ff9800' : '#f44336',
                  mx: 2
                }}
              >
                {question.difficulty}
              </Typography>
              <Link 
                href={question.solution} 
                target="_blank" 
                rel="noopener noreferrer"
                sx={{ 
                  display: 'flex', 
                  alignItems: 'center',
                  color: 'text.secondary',
                  '&:hover': {
                    color: 'primary.main'
                  }
                }}
              >
                <CodeIcon fontSize="small" />
              </Link>
            </Box>
          ))}
        </Box>
      </CardContent>
    </Card>
  );
};

export default PatternCard;
