import React from 'react';
import { Box, Container, Typography, Card, CardContent, CardMedia, Grid } from '@mui/material';

interface CompaniesProps {
  darkMode: boolean;
}

const companies = [
  {
    name: 'Google',
    logo: 'https://www.google.com/images/branding/googlelogo/1x/googlelogo_color_272x92dp.png',
    description: 'Search Engine & Technology Company'
  },
  {
    name: 'Amazon',
    logo: 'https://upload.wikimedia.org/wikipedia/commons/a/a9/Amazon_logo.svg',
    description: 'E-commerce & Cloud Computing'
  },
  {
    name: 'Netflix',
    logo: 'https://upload.wikimedia.org/wikipedia/commons/0/08/Netflix_2015_logo.svg',
    description: 'Streaming Service & Entertainment'
  },
  {
    name: 'Meta',
    logo: 'https://upload.wikimedia.org/wikipedia/commons/7/7b/Meta_Platforms_Inc._logo.svg',
    description: 'Social Media & Technology'
  },
  {
    name: 'Microsoft',
    logo: 'https://upload.wikimedia.org/wikipedia/commons/9/96/Microsoft_logo_%282012%29.svg',
    description: 'Software & Technology'
  }
];

const Companies: React.FC<CompaniesProps> = ({ darkMode }) => {
  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Typography 
        variant="h4" 
        component="h1" 
        sx={{ 
          mb: 4, 
          textAlign: 'center',
          color: darkMode ? '#ffffff' : '#333333',
          fontWeight: 'bold'
        }}
      >
        Top Tech Companies
      </Typography>
      
      <Grid container spacing={3}>
        {companies.map((company) => (
          <Grid item xs={12} sm={6} md={4} key={company.name}>
            <Card 
              sx={{ 
                height: '100%',
                display: 'flex',
                flexDirection: 'column',
                bgcolor: darkMode ? '#262626' : '#ffffff',
                transition: 'transform 0.2s',
                '&:hover': {
                  transform: 'scale(1.02)',
                  boxShadow: 6
                }
              }}
            >
              <CardMedia
                component="img"
                height="140"
                image={company.logo}
                alt={company.name}
                sx={{ 
                  objectFit: 'contain',
                  p: 2,
                  bgcolor: darkMode ? '#1a1a1a' : '#f5f5f5'
                }}
              />
              <CardContent>
                <Typography 
                  gutterBottom 
                  variant="h5" 
                  component="div"
                  sx={{ 
                    color: darkMode ? '#ffffff' : '#333333',
                    fontWeight: 'bold'
                  }}
                >
                  {company.name}
                </Typography>
                <Typography 
                  variant="body2" 
                  color="text.secondary"
                  sx={{ color: darkMode ? '#bdbdbd' : '#666666' }}
                >
                  {company.description}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};

export default Companies;
