import React from 'react';
import { Container, Typography, Card, CardContent, Link as MuiLink, Box } from '@mui/material';
import { companiesData } from '../data/companies';
import { Link as RouterLink } from 'react-router-dom';

interface CompaniesProps {
  darkMode: boolean;
}

const Companies: React.FC<CompaniesProps> = ({ darkMode }) => {
  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Typography variant="h4" gutterBottom sx={{ color: darkMode ? '#fff' : '#000' }}>
        Companies
      </Typography>
      <Box sx={{ 
        display: 'grid', 
        gridTemplateColumns: { 
          xs: '1fr', 
          sm: 'repeat(2, 1fr)', 
          md: 'repeat(3, 1fr)' 
        }, 
        gap: 3 
      }}>
        {companiesData.map((company) => (
          <Box key={company.name}>
            <Card sx={{ height: '100%', backgroundColor: darkMode ? '#333' : '#fff', color: darkMode ? '#fff' : '#000' }}>
              <CardContent>
                <MuiLink component={RouterLink} to={`/companies/${company.name.toLowerCase()}`} underline="none" color="inherit">
                  <Typography variant="h6">
                    {company.name}
                  </Typography>
                </MuiLink>
              </CardContent>
            </Card>
          </Box>
        ))}
      </Box>
    </Container>
  );
};

export default Companies;
