import React, { useState, useEffect } from 'react';
import {
  Container,
  Typography,
  TextField,
  Box,
  Grid,
  AppBar,
  Toolbar,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  SelectChangeEvent,
  Link,
  IconButton,
  LinearProgress,
  Switch,
  FormControlLabel,
  CircularProgress
} from '@mui/material';
import GitHubIcon from '@mui/icons-material/GitHub';
import YouTubeIcon from '@mui/icons-material/YouTube';
import PersonIcon from '@mui/icons-material/Person';
import MenuIcon from '@mui/icons-material/Menu';
import SearchIcon from '@mui/icons-material/Search';
import StarBorderIcon from '@mui/icons-material/StarBorder';
import StarIcon from '@mui/icons-material/Star';
import RefreshIcon from '@mui/icons-material/Refresh';
import DoneAllIcon from '@mui/icons-material/DoneAll';

import { patterns as initialPatterns, Pattern, Question } from './data/patterns';
import PatternCard from './components/PatternCard';

const LOCAL_STORAGE_KEY = 'dsaPatternsProgress';

function App() {
  const [darkMode, setDarkMode] = useState(true);
  const [patterns, setPatterns] = useState<Pattern[]>(() => {
    const savedPatterns = localStorage.getItem(LOCAL_STORAGE_KEY);
    return savedPatterns ? JSON.parse(savedPatterns) : initialPatterns;
  });
  const [searchTerm, setSearchTerm] = useState('');
  const [difficultyFilter, setDifficultyFilter] = useState<string>('all');
  const [statusFilter, setStatusFilter] = useState<string>('all');
  const [patternFilter, setPatternFilter] = useState<string>('all');
  const [languageFilter, setLanguageFilter] = useState<string>('all');
  const [showStarredOnly, setShowStarredOnly] = useState(false);
  const [starredPatterns, setStarredPatterns] = useState<Set<string>>(new Set());
  const [overallProgress, setOverallProgress] = useState(0);
  const [difficultyProgress, setDifficultyProgress] = useState({
    easy: { completed: 0, total: 0, percentage: 0 },
    medium: { completed: 0, total: 0, percentage: 0 },
    hard: { completed: 0, total: 0, percentage: 0 },
  });

  useEffect(() => {
    localStorage.setItem(LOCAL_STORAGE_KEY, JSON.stringify(patterns));
    let totalQuestions = 0;
    let completedQuestions = 0;
    let easyCompleted = 0, easyTotal = 0;
    let mediumCompleted = 0, mediumTotal = 0;
    let hardCompleted = 0, hardTotal = 0;

    patterns.forEach(pattern => {
      totalQuestions += pattern.questions.length;
      completedQuestions += pattern.questions.filter(q => q.status === 'Completed').length;

      pattern.questions.forEach(question => {
        if (question.difficulty === 'Easy') {
          easyTotal++;
          if (question.status === 'Completed') easyCompleted++;
        } else if (question.difficulty === 'Medium') {
          mediumTotal++;
          if (question.status === 'Completed') mediumCompleted++;
        } else if (question.difficulty === 'Hard') {
          hardTotal++;
          if (question.status === 'Completed') hardCompleted++;
        }
      });
    });

    setOverallProgress(totalQuestions === 0 ? 0 : (completedQuestions / totalQuestions) * 100);
    setDifficultyProgress({
      easy: { completed: easyCompleted, total: easyTotal, percentage: easyTotal === 0 ? 0 : (easyCompleted / easyTotal) * 100 },
      medium: { completed: mediumCompleted, total: mediumTotal, percentage: mediumTotal === 0 ? 0 : (mediumCompleted / mediumTotal) * 100 },
      hard: { completed: hardCompleted, total: hardTotal, percentage: hardTotal === 0 ? 0 : (hardCompleted / hardTotal) * 100 },
    });
  }, [patterns]);

  const handleSearchChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setSearchTerm(event.target.value);
  };

  const handleDifficultyChange = (event: SelectChangeEvent) => {
    setDifficultyFilter(event.target.value);
  };

  const handleStatusChange = (event: SelectChangeEvent) => {
    setStatusFilter(event.target.value);
  };

  const handlePatternChange = (event: SelectChangeEvent) => {
    setPatternFilter(event.target.value);
  };

  const handleLanguageChange = (event: SelectChangeEvent) => {
    setLanguageFilter(event.target.value);
  };

  const toggleStarredOnly = () => {
    setShowStarredOnly(prev => !prev);
  };

  const toggleStarPattern = (patternName: string) => {
    setStarredPatterns(prev => {
      const newSet = new Set(prev);
      if (newSet.has(patternName)) {
        newSet.delete(patternName);
      } else {
        newSet.add(patternName);
      }
      return newSet;
    });
  };

  const handleQuestionStatusChange = React.useCallback((patternName: string, questionName: string, newStatus: Question['status']) => {
    setPatterns(prevPatterns => {
      return prevPatterns.map(pattern => {
        if (pattern.name === patternName) {
          const updatedQuestions = pattern.questions.map(question => {
            if (question.name === questionName) {
              return { ...question, status: newStatus };
            }
            return question;
          });

          // Calculate new progress
          const completedQuestions = updatedQuestions.filter(q => q.status === 'Completed').length;
          return { 
            ...pattern, 
            questions: updatedQuestions,
            progress: {
              completed: completedQuestions,
              total: updatedQuestions.length
            }
          };
        }
        return pattern;
      });
    });
  }, []);

  const toggleStarQuestion = (patternName: string, questionName: string) => {
    setPatterns(prevPatterns => {
      return prevPatterns.map(pattern => {
        if (pattern.name === patternName) {
          const updatedQuestions = pattern.questions.map(question => {
            if (question.name === questionName) {
              return { ...question, starred: !question.starred };
            }
            return question;
          });
          return { ...pattern, questions: updatedQuestions };
        }
        return pattern;
      });
    });
  };

  const filteredPatterns = patterns.filter(pattern => {
    const matchesPattern = patternFilter === 'all' || pattern.name === patternFilter;

    const questionsMatchFilters = pattern.questions.filter(question => {
      const matchesSearch = searchTerm === '' || question.name.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesDifficulty = difficultyFilter === 'all' || question.difficulty === difficultyFilter;
      const matchesStatus = statusFilter === 'all' || question.status === statusFilter;
      const matchesStarred = !showStarredOnly || question.starred;

      return matchesSearch && matchesDifficulty && matchesStatus && matchesStarred;
    });

    return matchesPattern && questionsMatchFilters.length > 0;
  }).map(pattern => {
    const questionsMatchFilters = pattern.questions.filter(question => {
      const matchesSearch = searchTerm === '' || question.name.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesDifficulty = difficultyFilter === 'all' || question.difficulty === difficultyFilter;
      const matchesStatus = statusFilter === 'all' || question.status === statusFilter;
      const matchesStarred = !showStarredOnly || question.starred;
      return matchesSearch && matchesDifficulty && matchesStatus && matchesStarred;
    });
    return { ...pattern, questions: questionsMatchFilters };
  });

  const memoizedToggleStarQuestion = React.useCallback((patternName: string, questionName: string) => {
    setPatterns(prevPatterns => {
      return prevPatterns.map(pattern => {
        if (pattern.name === patternName) {
          const updatedQuestions = pattern.questions.map(question => {
            if (question.name === questionName) {
              return { ...question, starred: !question.starred };
            }
            return question;
          });
          return { ...pattern, questions: updatedQuestions };
        }
        return pattern;
      });
    });
  }, []);

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  return (
    <Box sx={{ 
      flexGrow: 1, 
      bgcolor: darkMode ? '#1a1a1a' : '#f5f5f5', 
      minHeight: '100vh', 
      color: darkMode ? '#e0e0e0' : '#333333' 
    }}>
      <AppBar position="static" sx={{ bgcolor: darkMode ? '#262626' : '#ffffff', boxShadow: 3 }}>
        <Toolbar>
          <Typography 
            variant="h6" 
            component="div" 
            sx={{ 
              flexGrow: 0, 
              fontWeight: 'bold', 
              color: darkMode ? '#ffffff' : '#333333', 
              letterSpacing: '0.1em',
              mr: 4
            }}>
            Algo<Box component="span" sx={{ color: '#00aaff' }}>Master</Box>
          </Typography>

          <Box sx={{ display: { xs: 'none', md: 'flex' }, alignItems: 'center', mr: 3, gap: 3 }}>
            <Link href="#" color="inherit" underline="none" sx={{ color: darkMode ? '#bdbdbd' : '#666666', fontWeight: 'medium', '&:hover': { color: darkMode ? '#ffffff' : '#333333' } }}>Learn</Link>
            <Link href="#" color="inherit" underline="none" sx={{ color: darkMode ? '#bdbdbd' : '#666666', fontWeight: 'medium', '&:hover': { color: darkMode ? '#ffffff' : '#333333' } }}>Practice</Link>
            <Link href="#" color="inherit" underline="none" sx={{ color: darkMode ? '#bdbdbd' : '#666666', fontWeight: 'medium', '&:hover': { color: darkMode ? '#ffffff' : '#333333' } }}>Newsletter</Link>
          </Box>

          <Box sx={{ flexGrow: 1 }} />

          <FormControlLabel
            control={<Switch checked={darkMode} onChange={toggleDarkMode} />}
            label={darkMode ? "Dark Mode" : "Light Mode"}
            sx={{ color: darkMode ? '#ffffff' : '#333333', mr: 2 }}
          />

          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1.5 }}>
            <IconButton color="inherit" href="#" target="_blank" sx={{ color: darkMode ? '#bdbdbd' : '#666666', '&:hover': { color: darkMode ? '#ffffff' : '#333333' } }}><YouTubeIcon fontSize="small"/></IconButton>
            <IconButton color="inherit" href="#" target="_blank" sx={{ color: darkMode ? '#bdbdbd' : '#666666', '&:hover': { color: darkMode ? '#ffffff' : '#333333' } }}><GitHubIcon fontSize="small"/></IconButton>
            <IconButton color="inherit" sx={{ color: darkMode ? '#bdbdbd' : '#666666', '&:hover': { color: darkMode ? '#ffffff' : '#333333' } }}><PersonIcon fontSize="small"/></IconButton>
            <IconButton color="inherit" sx={{ display: { xs: 'block', md: 'none' }, color: darkMode ? '#bdbdbd' : '#666666', '&:hover': { color: darkMode ? '#ffffff' : '#333333' } }}><MenuIcon fontSize="small"/></IconButton>
          </Box>
        </Toolbar>
      </AppBar>

      <Container maxWidth={false} sx={{ mt: 4, mb: 4, px: { xs: 2, sm: 3, md: 4 } }}>
        {/* Main content grid for Filters and Overall Progress */}
        <Grid container spacing={3} sx={{ width: '100%', mb: 4 }}> {/* Main grid takes full width and has bottom margin */}
          {/* Combined Filters and Progress Card */}
          <Grid item xs={12}> {/* This card should take full width of the main grid */}
            <Box 
              sx={{
                bgcolor: darkMode ? '#212121' : '#ffffff',
                p: 3,
                borderRadius: 2,
                boxShadow: 1,
                height: '100%'
              }}
            >
              {/* Filters Content */}
              <Typography variant="h6" component="div" sx={{ mb: 2, fontWeight: 'bold', color: darkMode ? '#ffffff' : '#333333' }}>Filters</Typography>
              <Grid container spacing={2}>
                <Grid item xs={12} sm={6} md={3}>
                    <Typography variant="caption" sx={{ display: 'block', mb: 0.5, fontWeight: 'medium', color: darkMode ? '#ffffff' : '#333333' }}>Search</Typography>
                    <TextField
                      size="small"
                      placeholder="Search problems..."
                      value={searchTerm}
                      onChange={handleSearchChange}
                      fullWidth
                      InputProps={{
                        startAdornment: (
                          <SearchIcon sx={{ color: darkMode ? '#ffffff' : '#333333', mr: 1 }} />
                        ),
                      }}
                      sx={{
                        bgcolor: darkMode ? '#121212' : '#ffffff',
                        '.MuiOutlinedInput-notchedOutline': { borderColor: darkMode ? '#424242' : '#e0e0e0' }, 
                        input: { color: darkMode ? '#ffffff' : '#333333' }, 
                        label: { color: darkMode ? '#ffffff' : '#333333' },
                        '&:hover .MuiOutlinedInput-notchedOutline': { borderColor: darkMode ? '#616161' : '#e0e0e0' },
                        '.Mui-focused .MuiOutlinedInput-notchedOutline': { borderColor: darkMode ? '#00aaff' : '#333333' }
                      }}
                      InputLabelProps={{ style: { color: darkMode ? '#ffffff' : '#333333' } }}
                    />
                </Grid>
                <Grid item xs={12} sm={6} md={3}>
                    <Typography variant="caption" sx={{ display: 'block', mb: 0.5, fontWeight: 'medium', color: darkMode ? '#ffffff' : '#333333' }}>Status</Typography>
                    <FormControl size="small" fullWidth>
                       <Select
                          value={statusFilter}
                          onChange={handleStatusChange}
                          displayEmpty
                           sx={{
                              bgcolor: darkMode ? '#121212' : '#ffffff', 
                              '.MuiOutlinedInput-notchedOutline': { borderColor: darkMode ? '#424242' : '#e0e0e0' }, 
                              '.MuiSelect-select': { color: darkMode ? '#ffffff' : '#333333' }, 
                              '.MuiSvgIcon-root': { color: darkMode ? '#ffffff' : '#333333' },
                              '&:hover .MuiOutlinedInput-notchedOutline': { borderColor: darkMode ? '#616161' : '#e0e0e0' },
                              '.Mui-focused .MuiOutlinedInput-notchedOutline': { borderColor: darkMode ? '#00aaff' : '#333333' }
                            }}
                        >
                          <MenuItem value="all">All</MenuItem>
                          <MenuItem value="Not Started">Not Started</MenuItem>
                          <MenuItem value="In Progress">In Progress</MenuItem>
                          <MenuItem value="Completed">Completed</MenuItem>
                        </Select>
                    </FormControl>
                </Grid>
                 <Grid item xs={12} sm={6} md={3}>
                     <Typography variant="caption" sx={{ display: 'block', mb: 0.5, fontWeight: 'medium', color: darkMode ? '#ffffff' : '#333333' }}>Difficulty</Typography>
                     <FormControl size="small" fullWidth>
                       <Select
                         value={difficultyFilter}
                         onChange={handleDifficultyChange}
                         displayEmpty
                          sx={{
                              bgcolor: darkMode ? '#121212' : '#ffffff', 
                              '.MuiOutlinedInput-notchedOutline': { borderColor: darkMode ? '#424242' : '#e0e0e0' }, 
                              '.MuiSelect-select': { color: darkMode ? '#ffffff' : '#333333' }, 
                              '.MuiSvgIcon-root': { color: darkMode ? '#ffffff' : '#333333' },
                              '&:hover .MuiOutlinedInput-notchedOutline': { borderColor: darkMode ? '#616161' : '#e0e0e0' },
                              '.Mui-focused .MuiOutlinedInput-notchedOutline': { borderColor: darkMode ? '#00aaff' : '#333333' }
                            }}
                       >
                         <MenuItem value="all">All</MenuItem>
                         <MenuItem value="Easy">Easy</MenuItem>
                         <MenuItem value="Medium">Medium</MenuItem>
                         <MenuItem value="Hard">Hard</MenuItem>
                       </Select>
                     </FormControl>
                 </Grid>
                 <Grid item xs={12} sm={6} md={3}>
                     <Typography variant="caption" sx={{ display: 'block', mb: 0.5, fontWeight: 'medium', color: darkMode ? '#ffffff' : '#333333' }}>Pattern</Typography>
                     <FormControl size="small" fullWidth>
                       <Select
                         value={patternFilter}
                         onChange={handlePatternChange}
                         displayEmpty
                          sx={{
                              bgcolor: darkMode ? '#121212' : '#ffffff', 
                              '.MuiOutlinedInput-notchedOutline': { borderColor: darkMode ? '#424242' : '#e0e0e0' }, 
                              '.MuiSelect-select': { color: darkMode ? '#ffffff' : '#333333' }, 
                              '.MuiSvgIcon-root': { color: darkMode ? '#ffffff' : '#333333' },
                              '&:hover .MuiOutlinedInput-notchedOutline': { borderColor: darkMode ? '#616161' : '#e0e0e0' },
                              '.Mui-focused .MuiOutlinedInput-notchedOutline': { borderColor: darkMode ? '#00aaff' : '#333333' }
                            }}
                       >
                         <MenuItem value="all">All Patterns</MenuItem>
                         {initialPatterns.map(pattern => (
                            <MenuItem key={pattern.name} value={pattern.name}>{pattern.name}</MenuItem>
                         ))}
                       </Select>
                     </FormControl>
                 </Grid>
                 <Grid item xs={12} sm={6} md={3}>
                     <Typography variant="caption" sx={{ display: 'block', mb: 0.5, fontWeight: 'medium', color: darkMode ? '#ffffff' : '#333333' }}>Preferred Language</Typography>
                     <FormControl size="small" fullWidth>
                       <Select
                         value={languageFilter}
                         onChange={handleLanguageChange}
                         displayEmpty
                          sx={{
                              bgcolor: darkMode ? '#121212' : '#ffffff', 
                              '.MuiOutlinedInput-notchedOutline': { borderColor: darkMode ? '#424242' : '#e0e0e0' }, 
                              '.MuiSelect-select': { color: darkMode ? '#ffffff' : '#333333' }, 
                              '.MuiSvgIcon-root': { color: darkMode ? '#ffffff' : '#333333' },
                              '&:hover .MuiOutlinedInput-notchedOutline': { borderColor: darkMode ? '#616161' : '#e0e0e0' },
                              '.Mui-focused .MuiOutlinedInput-notchedOutline': { borderColor: darkMode ? '#00aaff' : '#333333' }
                            }}
                       >
                         <MenuItem value="all">All Languages</MenuItem>
                         <MenuItem value="cpp">C++</MenuItem>
                         <MenuItem value="python">Python</MenuItem>
                       </Select>
                     </FormControl>
                 </Grid>
                 <Grid item xs={12} sm={6} md={3} sx={{ display: 'flex', alignItems: 'flex-end', gap: 1 }}>
                    <Typography variant="caption" sx={{ display: 'block', mb: 0.5, fontWeight: 'medium', color: darkMode ? '#ffffff' : '#333333' }}>Starred Only</Typography>
                    <IconButton 
                      onClick={toggleStarredOnly} 
                      sx={{ color: showStarredOnly ? darkMode ? '#ffeb3b' : '#ffc107' : darkMode ? '#bdbdbd' : '#ffffff', '&:hover': { color: showStarredOnly ? darkMode ? '#ffc107' : '#ffeb3b' : darkMode ? '#ffffff' : '#ffffff' } }}
                    >
                        {showStarredOnly ? <StarIcon /> : <StarBorderIcon />}
                    </IconButton>
                    <IconButton sx={{ color: darkMode ? '#bdbdbd' : '#ffffff', '&:hover': { color: darkMode ? '#ffffff' : '#ffffff' } }}><RefreshIcon /></IconButton>
                 </Grid>
              </Grid>

              {/* Progress Content */}
              <Typography variant="h6" component="div" sx={{ mt: 4, mb: 2, fontWeight: 'bold', color: darkMode ? '#ffffff' : '#333333' }}>Progress by Difficulty</Typography>
              <Box sx={{ mb: 2 }}>
                <Typography variant="body2" sx={{ mb: 0.5, color: darkMode ? '#ffffff' : '#333333' }}>Easy: {difficultyProgress.easy.completed} / {difficultyProgress.easy.total}</Typography>
                <LinearProgress variant="determinate" value={difficultyProgress.easy.percentage} sx={{ height: 6, borderRadius: 3, bgcolor: darkMode ? '#424242' : '#e0e0e0', '& .MuiLinearProgress-bar': { bgcolor: '#66bb6a' } }} />
              </Box>
              <Box sx={{ mb: 2 }}>
                <Typography variant="body2" sx={{ mb: 0.5, color: darkMode ? '#ffffff' : '#333333' }}>Medium: {difficultyProgress.medium.completed} / {difficultyProgress.medium.total}</Typography>
                <LinearProgress variant="determinate" value={difficultyProgress.medium.percentage} sx={{ height: 6, borderRadius: 3, bgcolor: darkMode ? '#424242' : '#e0e0e0', '& .MuiLinearProgress-bar': { bgcolor: '#ffa726' } }} />
              </Box>
              <Box>
                <Typography variant="body2" sx={{ mb: 0.5, color: darkMode ? '#ffffff' : '#333333' }}>Hard: {difficultyProgress.hard.completed} / {difficultyProgress.hard.total}</Typography>
                <LinearProgress variant="determinate" value={difficultyProgress.hard.percentage} sx={{ height: 6, borderRadius: 3, bgcolor: darkMode ? '#424242' : '#e0e0e0', '& .MuiLinearProgress-bar': { bgcolor: '#ef5350' } }} />
              </Box>
            </Box>
          </Grid>

          {/* Combined Progress and Difficulty Stats Card */}
          <Grid item xs={12}> {/* Use a full-width grid item for this combined card */}
            <Box
              sx={{
                bgcolor: darkMode ? '#212121' : '#ffffff',
                p: 3,
                borderRadius: 2,
                boxShadow: 1,
                height: '100%', // Ensure it takes full height if in a flex container or grid item
                display: 'flex', // Use flexbox to arrange internal items (circular progress and difficulty counts)
                flexDirection: { xs: 'column', md: 'row' }, // Stack vertically on small screens, side-by-side on medium/large
                alignItems: 'center', // Center items vertically
                gap: { xs: 4, md: 8 }, // Add space between items
              }}
            >
              {/* Circular Overall Progress */}
              <Box sx={{ position: 'relative', display: 'inline-flex', flexShrink: 0 }}> {/* flexShrink prevents it from shrinking */}
                <CircularProgress
                  variant="determinate"
                  value={overallProgress}
                  size={150} // Adjust size as needed
                  thickness={4} // Adjust thickness as needed
                  sx={{
                    color: darkMode ? '#66bb6a' : '#4caf50', // Green color for progress
                    // Optional: Add a background circle
                    // '& .MuiCircularProgress-circle': {
                    //   strokeLinecap: 'round',
                    // },
                  }}
                />
                <Box
                  sx={{
                    top: 0,
                    left: 0,
                    bottom: 0,
                    right: 0,
                    position: 'absolute',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    flexDirection: 'column',
                  }}
                >
                  <Typography variant="h4" component="div" color="text.secondary" sx={{ color: darkMode ? '#ffffff' : '#333333', fontWeight: 'bold' }}>
                    {patterns.reduce((sum, pattern) => sum + pattern.questions.filter(q => q.status === 'Completed').length, 0)}<Box component="span" sx={{ fontSize: '0.7em', color: darkMode ? '#bdbdbd' : '#666666' }}>/{patterns.reduce((sum, pattern) => sum + pattern.questions.length, 0)}</Box>
                  </Typography>
                  <Typography variant="body2" color="text.secondary" sx={{ color: darkMode ? '#bdbdbd' : '#666666' }}>
                    Solved
                  </Typography>
                </Box>
              </Box>

              {/* Difficulty Counts */}
              <Box display="flex" flexDirection="column" gap={1.5} sx={{ flexGrow: 1 }}> {/* flexGrow allows it to take available space */}
                <Box sx={{ bgcolor: darkMode ? '#333333' : '#eeeeee', p: 1.5, borderRadius: 1, textAlign: 'center' }}>
                  <Typography variant="body1" sx={{ fontWeight: 'bold', color: '#66bb6a' }}>Easy</Typography>
                  <Typography variant="body2" sx={{ color: darkMode ? '#ffffff' : '#333333' }}>{difficultyProgress.easy.completed}/{difficultyProgress.easy.total}</Typography>
                </Box>
                <Box sx={{ bgcolor: darkMode ? '#333333' : '#eeeeee', p: 1.5, borderRadius: 1, textAlign: 'center' }}>
                  <Typography variant="body1" sx={{ fontWeight: 'bold', color: '#ffa726' }}>Med.</Typography>
                  <Typography variant="body2" sx={{ color: darkMode ? '#ffffff' : '#333333' }}>{difficultyProgress.medium.completed}/{difficultyProgress.medium.total}</Typography>
                </Box>
                <Box sx={{ bgcolor: darkMode ? '#333333' : '#eeeeee', p: 1.5, borderRadius: 1, textAlign: 'center' }}>
                  <Typography variant="body1" sx={{ fontWeight: 'bold', color: '#ef5350' }}>Hard</Typography>
                  <Typography variant="body2" sx={{ color: darkMode ? '#ffffff' : '#333333' }}>{difficultyProgress.hard.completed}/{difficultyProgress.hard.total}</Typography>
                </Box>
              </Box>
            </Box>
          </Grid>
        </Grid>

        {/* Pattern Cards Container - One per row, full width */}
        <Box sx={{ width: '100%', mt: 8 }}> {/* Container box for pattern cards set to full width with increased top margin */}
          {filteredPatterns.map((pattern) => (
            <Box key={pattern.name} sx={{ mb: 3, width: '100%' }}> {/* Box for each card with bottom margin and full width */}
              <PatternCard
                pattern={pattern}
                isStarred={starredPatterns.has(pattern.name)}
                onToggleStar={() => toggleStarPattern(pattern.name)}
                onQuestionStatusChange={handleQuestionStatusChange}
                onToggleStarQuestion={memoizedToggleStarQuestion}
                darkMode={darkMode}
              />
            </Box>
          ))}
        </Box>
      </Container>
    </Box>
  );
}

export default App;
